import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useSelector } from "react-redux";
import { useSnackbar } from "notistack";
import axios from "axios";
import {
  Box,
  Typography,
  Card,
  CardContent,
  Grid,
  Paper,
  Button,
  TextField,
  CircularProgress,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TablePagination,
  InputAdornment,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Chip,
  Divider,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
} from "@mui/material";
import {
  Search,
  School,
  CalendarToday,
  Class,
  Add,
  BarChart,
} from "@mui/icons-material";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

const TeacherClassesPage = () => {
  const navigate = useNavigate();
  const { enqueueSnackbar } = useSnackbar();
  const { user, token } = useSelector((state) => state.auth);

  // State cho dữ liệu
  const [classes, setClasses] = useState([]);
  const [semesters, setSemesters] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [totalClasses, setTotalClasses] = useState(0);

  // State cho tìm kiếm và phân trang
  const [search, setSearch] = useState("");
  const [semester, setSemester] = useState("");
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);

  // State cho modal tạo lớp mới
  const [openCreateModal, setOpenCreateModal] = useState(false);
  const [isCreating, setIsCreating] = useState(false);
  const [departments, setDepartments] = useState([]);
  const [newClass, setNewClass] = useState({
    name: "",
    code: "",
    department_id: "",
    year_start: new Date().getFullYear(),
    year_end: new Date().getFullYear() + 4,
  });

  // Load dữ liệu ban đầu
  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        setIsLoading(true);

        // Load các học kỳ
        const semestersResponse = await axios.get(`${API_URL}/semesters`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setSemesters(semestersResponse.data.data || []);

        // Load lớp học theo mặc định
        await fetchClasses();
      } catch (error) {
        console.error("Lỗi khi tải dữ liệu:", error);
        enqueueSnackbar("Lỗi khi tải dữ liệu", { variant: "error" });
      } finally {
        setIsLoading(false);
      }
    };

    fetchInitialData();
  }, [token, enqueueSnackbar]);

  // Tải danh sách lớp học
  const fetchClasses = async () => {
    try {
      setIsLoading(true);

      let url = `${API_URL}/classes/teaching/teacher/${user._id}?page=${
        page + 1
      }&limit=${rowsPerPage}`;

      if (search) {
        url += `&search=${encodeURIComponent(search)}`;
      }

      if (semester) {
        url += `&semester_id=${semester}`;
      }

      const response = await axios.get(url, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setClasses(response.data.data || []);
      setTotalClasses(response.data.count || 0);
    } catch (error) {
      console.error("Lỗi khi tải danh sách lớp học:", error);
      enqueueSnackbar("Lỗi khi tải danh sách lớp học", {
        variant: "error",
      });
    } finally {
      setIsLoading(false);
    }
  };

  // Xử lý thay đổi trang hoặc số dòng mỗi trang
  useEffect(() => {
    fetchClasses();
  }, [page, rowsPerPage, token]);

  // Xử lý tìm kiếm
  const handleSearch = () => {
    setPage(0); // Reset về trang đầu tiên
    fetchClasses();
  };

  // Xử lý thay đổi trang
  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  // Xử lý thay đổi số dòng mỗi trang
  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  // Xử lý khi nhấn Enter trong ô tìm kiếm
  const handleSearchKeyDown = (e) => {
    if (e.key === "Enter") {
      handleSearch();
    }
  };

  // Lọc theo học kỳ hoặc năm học
  const handleFilterChange = () => {
    setPage(0);
    fetchClasses();
  };

  // Mở trang chi tiết lớp học
  const handleViewClass = (classId) => {
    navigate(`/teacher/classes/${classId}`);
  };

  // Tải danh sách khoa
  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const response = await axios.get(`${API_URL}/departments`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setDepartments(response.data.data || []);
      } catch (error) {
        console.error("Lỗi khi tải danh sách khoa:", error);
      }
    };

    fetchDepartments();
  }, [token]);

  // Mở modal tạo lớp mới
  const handleOpenCreateModal = () => {
    setOpenCreateModal(true);
  };

  // Đóng modal tạo lớp mới
  const handleCloseCreateModal = () => {
    setOpenCreateModal(false);
    setNewClass({
      name: "",
      code: "",
      department_id: "",
      year_start: new Date().getFullYear(),
      year_end: new Date().getFullYear() + 4,
    });
  };

  // Xử lý thay đổi trong form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewClass({
      ...newClass,
      [name]: value,
    });
  };

  // Tạo lớp mới
  const handleCreateClass = async () => {
    try {
      setIsCreating(true);

      // Validate
      if (!newClass.name || !newClass.code || !newClass.department_id) {
        enqueueSnackbar("Vui lòng điền đầy đủ thông tin", {
          variant: "warning",
        });
        setIsCreating(false);
        return;
      }

      // Gọi API tạo lớp
      const response = await axios.post(
        `${API_URL}/classes/main/create-as-advisor`,
        newClass,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      enqueueSnackbar(response.data.message || "Tạo lớp mới thành công", {
        variant: "success",
      });

      // Đóng modal và tải lại danh sách
      handleCloseCreateModal();
      // Tải lại danh sách lớp nếu cần

      // Chuyển đến trang quản lý lớp chủ nhiệm
      navigate("/teacher/main-classes");
    } catch (error) {
      console.error("Lỗi khi tạo lớp mới:", error);
      enqueueSnackbar(error.response?.data?.message || "Lỗi khi tạo lớp mới", {
        variant: "error",
      });
    } finally {
      setIsCreating(false);
    }
  };

  return (
    <Box sx={{ padding: 3 }}>
      <Typography variant="h4" gutterBottom>
        Quản lý lớp học
      </Typography>

      {/* Bộ lọc và tìm kiếm */}
      <Paper sx={{ p: 2, mb: 3 }}>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={12} sm={5}>
            <TextField
              fullWidth
              label="Tìm kiếm lớp học"
              variant="outlined"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              onKeyDown={handleSearchKeyDown}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <Search />
                  </InputAdornment>
                ),
              }}
            />
          </Grid>
          <Grid item xs={12} sm={5}>
            <FormControl fullWidth variant="outlined">
              <InputLabel>Học kỳ</InputLabel>
              <Select
                value={semester}
                onChange={(e) => {
                  setSemester(e.target.value);
                  handleFilterChange();
                }}
                label="Học kỳ"
              >
                <MenuItem value="">Tất cả học kỳ</MenuItem>
                {semesters.map((sem) => (
                  <MenuItem key={sem._id} value={sem._id}>
                    {sem.name} (
                    {sem.academic_year || `${sem.year}-${sem.year + 1}`})
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} sm={2}>
            <Button
              variant="contained"
              color="primary"
              onClick={handleSearch}
              fullWidth
            >
              Tìm kiếm
            </Button>
          </Grid>
          <Grid item xs={12} sm={12} sx={{ mt: 2, textAlign: "right" }}>
            <Button
              variant="contained"
              color="success"
              startIcon={<Add />}
              onClick={handleOpenCreateModal}
            >
              Tạo lớp chủ nhiệm mới
            </Button>
          </Grid>
        </Grid>
      </Paper>

      {/* Danh sách lớp học */}
      <Paper>
        {isLoading ? (
          <Box sx={{ display: "flex", justifyContent: "center", p: 3 }}>
            <CircularProgress />
          </Box>
        ) : (
          <>
            <TableContainer>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Tên lớp</TableCell>
                    <TableCell>Môn học</TableCell>
                    <TableCell>Kỳ học</TableCell>
                    <TableCell>Lớp chính</TableCell>
                    <TableCell align="center">Số lượng SV</TableCell>
                    <TableCell>Trạng thái</TableCell>
                    <TableCell>Thao tác</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {classes.length > 0 ? (
                    classes.map((classItem) => (
                      <TableRow key={classItem._id}>
                        <TableCell>
                          <Typography variant="body2">
                            {classItem.class_name}
                          </Typography>
                        </TableCell>
                        <TableCell>
                          {classItem.subject_id ? (
                            <Box>
                              <Typography variant="body2">
                                {classItem.subject_id.name}
                              </Typography>
                              <Typography
                                variant="caption"
                                color="textSecondary"
                              >
                                {classItem.subject_id.code}
                              </Typography>
                            </Box>
                          ) : (
                            <Chip label="N/A" size="small" variant="outlined" />
                          )}
                        </TableCell>
                        <TableCell>
                          {classItem.semester_id ? (
                            <Chip
                              label={classItem.semester_id.name}
                              size="small"
                              color="info"
                              variant="outlined"
                            />
                          ) : (
                            <Chip
                              label="Không xác định"
                              size="small"
                              variant="outlined"
                              color="default"
                            />
                          )}
                        </TableCell>
                        <TableCell>
                          {classItem.main_class_id ? (
                            <Chip
                              label={classItem.main_class_id.name}
                              size="small"
                              sx={{ bgcolor: "#e3f2fd" }}
                            />
                          ) : (
                            <Chip
                              label="Không có"
                              size="small"
                              variant="outlined"
                              color="default"
                            />
                          )}
                        </TableCell>
                        <TableCell align="center">
                          <Chip
                            label={classItem.students?.length || 0}
                            color={
                              classItem.students?.length > 0
                                ? "success"
                                : "default"
                            }
                            size="small"
                          />
                        </TableCell>
                        <TableCell>
                          {classItem.status === "đang học" ? (
                            <Chip
                              label="Đang học"
                              color="success"
                              size="small"
                            />
                          ) : classItem.status === "đã kết thúc" ? (
                            <Chip
                              label="Đã kết thúc"
                              color="default"
                              size="small"
                            />
                          ) : (
                            <Chip
                              label="Chưa bắt đầu"
                              color="primary"
                              size="small"
                            />
                          )}
                        </TableCell>
                        <TableCell>
                          <Button
                            variant="outlined"
                            size="small"
                            onClick={() => handleViewClass(classItem._id)}
                          >
                            Chi tiết
                          </Button>
                        </TableCell>
                      </TableRow>
                    ))
                  ) : (
                    <TableRow>
                      <TableCell colSpan={7} align="center">
                        Không có lớp học nào.
                      </TableCell>
                    </TableRow>
                  )}
                </TableBody>
              </Table>
            </TableContainer>
            <TablePagination
              rowsPerPageOptions={[5, 10, 25]}
              component="div"
              count={totalClasses}
              rowsPerPage={rowsPerPage}
              page={page}
              onPageChange={handleChangePage}
              onRowsPerPageChange={handleChangeRowsPerPage}
              labelRowsPerPage="Dòng mỗi trang:"
              labelDisplayedRows={({ from, to, count }) =>
                `${from}-${to} trong số ${count !== -1 ? count : `hơn ${to}`}`
              }
            />
          </>
        )}
      </Paper>

      {/* Dialog tạo lớp mới */}
      <Dialog
        open={openCreateModal}
        onClose={handleCloseCreateModal}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>Tạo lớp chủ nhiệm mới</DialogTitle>
        <DialogContent>
          <DialogContentText sx={{ mb: 2 }}>
            Nhập thông tin để tạo lớp chủ nhiệm mới. Bạn sẽ tự động trở thành
            giáo viên chủ nhiệm của lớp này.
          </DialogContentText>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Tên lớp"
                name="name"
                value={newClass.name}
                onChange={handleInputChange}
                required
                margin="dense"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Mã lớp"
                name="code"
                value={newClass.code}
                onChange={handleInputChange}
                required
                margin="dense"
                helperText="Mã lớp phải là duy nhất"
              />
            </Grid>
            <Grid item xs={12}>
              <FormControl fullWidth margin="dense">
                <InputLabel>Khoa</InputLabel>
                <Select
                  name="department_id"
                  value={newClass.department_id}
                  onChange={handleInputChange}
                  label="Khoa"
                  required
                >
                  {departments.map((dept) => (
                    <MenuItem key={dept._id} value={dept._id}>
                      {dept.name}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={6}>
              <TextField
                fullWidth
                label="Năm bắt đầu"
                name="year_start"
                type="number"
                value={newClass.year_start}
                onChange={handleInputChange}
                margin="dense"
              />
            </Grid>
            <Grid item xs={6}>
              <TextField
                fullWidth
                label="Năm kết thúc"
                name="year_end"
                type="number"
                value={newClass.year_end}
                onChange={handleInputChange}
                margin="dense"
              />
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseCreateModal} color="inherit">
            Hủy
          </Button>
          <Button
            onClick={handleCreateClass}
            color="primary"
            variant="contained"
            disabled={isCreating}
            startIcon={isCreating ? <CircularProgress size={20} /> : null}
          >
            {isCreating ? "Đang tạo..." : "Tạo lớp"}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default TeacherClassesPage;
