      [field]: value,
    };
    setScheduleDays(updatedSchedule);

    // Tính lại ngày kết thúc khi lịch học thay đổi
    setEditClassData((prev) => ({
      ...prev,
      course_end_date: calculateEndDate(
        prev.course_start_date,
        updatedSchedule,
        prev.total_sessions
      ),
    }));

    // Kiểm tra xung đột nếu thay đổi phòng học hoặc thời gian
    if (
      field === "room_id" ||
      field === "day_of_week" ||
      field === "start_period" ||
      field === "end_period" ||
      field === "start_time" ||
      field === "end_time"
    ) {
      // Đặt timeout để tránh gọi API quá nhiều khi người dùng thay đổi nhiều lần
      setTimeout(() => {
        if (
          updatedSchedule[index].room_id &&
          updatedSchedule[index].day_of_week !== undefined
        ) {
          checkConflicts(updatedSchedule);
        }
      }, 500);
    }
  };

  // Cập nhật hàm handleUpdateClass
  const handleUpdateClass = async () => {
    if (!editClassData.class_name || !editClassData.class_code) {
      enqueueSnackbar("Vui lòng điền đầy đủ thông tin bắt buộc", {
        variant: "warning",
      });
      return;
    }

    // Kiểm tra room_id trong từng schedule item
    for (const day of scheduleDays) {
      if (!day.room_id) {
        enqueueSnackbar(
          `Vui lòng chọn phòng học cho buổi học vào ${getDayOfWeekName(
            day.day_of_week
          )}`,
          { variant: "warning" }
        );
        return;
      }
    }

    // Kiểm tra xung đột lịch cuối cùng trước khi cập nhật lớp
    try {
      setCheckingConflicts(true);
      const response = await checkScheduleConflicts({
        teacher_id: classInfo.teacher_id._id,
        schedule: scheduleDays,
        class_id: id,
      });

      if (response.data.has_conflicts) {
        setConflictWarnings(response.data.conflicts);

        // Hiển thị thông báo xác nhận nếu có xung đột
        if (
          window.confirm(
            "Phát hiện xung đột lịch học. Bạn vẫn muốn cập nhật lịch học này không?\n\n" +
              response.data.conflicts.map((c) => c.message).join("\n")
          )
        ) {
          // Tiếp tục cập nhật lớp nếu người dùng xác nhận
          await updateClassData();
        }
      } else {
        // Không có xung đột, cập nhật lớp
        await updateClassData();
      }
    } catch (error) {
      console.error("Lỗi khi kiểm tra xung đột lịch học:", error);
      // Vẫn cập nhật lớp ngay cả khi không thể kiểm tra xung đột
      await updateClassData();
    } finally {
      setCheckingConflicts(false);
    }
  };

  // Tách logic cập nhật lớp thành hàm riêng
  const updateClassData = async () => {
    setIsUpdating(true);
    try {
      // Tạo đối tượng dữ liệu để cập nhật
      const updateData = {
        ...editClassData,
        schedule: scheduleDays,
      };

      // Xóa các trường không cần thiết
      delete updateData.teacher_id;
      delete updateData.createdAt;
      delete updateData.updatedAt;

      await updateTeachingClass(id, updateData);

      enqueueSnackbar("Cập nhật lớp học thành công", {
        variant: "success",
      });
      setEditMode(false);
      fetchClassData(); // Tải lại dữ liệu lớp học
      setConflictWarnings([]); // Xóa cảnh báo xung đột
    } catch (error) {
      console.error("Lỗi khi cập nhật lớp học:", error);
      enqueueSnackbar(
        error.response?.data?.message || "Lỗi khi cập nhật lớp học",
        { variant: "error" }
      );
    } finally {
      setIsUpdating(false);
    }
  };

  // Lấy thứ trong tuần từ ngày
  const getDayOfWeekFromDate = (dateString) => {
    if (!dateString) return 1; // Mặc định là thứ 2 nếu không có ngày

    const date = new Date(dateString);
    // getDay() trả về 0 cho Chủ Nhật, 1-6 cho Thứ 2 đến Thứ 7
    const day = date.getDay();

    // Chuyển đổi để 0 = Chủ Nhật, 1 = Thứ Hai, ...
    return day;
  };

  // Lấy tên của thứ trong tuần
  const getDayOfWeekName = (dayOfWeek) => {
    const days = [
      "Chủ Nhật",
      "Thứ Hai",
      "Thứ Ba",
      "Thứ Tư",
      "Thứ Năm",
      "Thứ Sáu",
      "Thứ Bảy",
    ];
    return days[dayOfWeek];
  };

  // Thêm lịch học
  const addScheduleDay = () => {
    // Lấy ngày bắt đầu
    const startDate = editClassData.course_start_date;
    if (!startDate) {
      enqueueSnackbar("Vui lòng chọn ngày bắt đầu trước", {
        variant: "warning",
      });
      return;
    }

    // Lấy thứ từ ngày bắt đầu
    const baseDayOfWeek = getDayOfWeekFromDate(startDate);

    // Tìm thứ tiếp theo chưa có trong lịch
    const existingDays = scheduleDays.map((day) => day.day_of_week);
    let nextDay = (baseDayOfWeek + 1) % 7; // Thứ tiếp theo

    // Tìm một thứ chưa có trong lịch hiện tại
    for (let i = 0; i < 7; i++) {
      if (!existingDays.includes(nextDay)) {
        break;
      }
      nextDay = (nextDay + 1) % 7;
    }

    // Nếu đã có đủ 7 ngày trong tuần, thì dùng thứ 2
    if (existingDays.length >= 7) {
      nextDay = 1;
    }

    const updatedSchedule = [
      ...scheduleDays,
      {
        day_of_week: nextDay,
        start_period: 1,
        end_period: 2,
        start_time: "07:00",
        end_time: "08:40",
        room_id: editClassData.room_id,
        is_recurring: true,
        specific_dates: [],
        excluded_dates: [],
      },
    ];

    setScheduleDays(updatedSchedule);

    // Tính lại ngày kết thúc sau khi thêm buổi học
    setEditClassData((prev) => ({
      ...prev,
      course_end_date: calculateEndDate(
        prev.course_start_date,
        updatedSchedule,
        prev.total_sessions
      ),
    }));
  };

  // Xóa lịch học
  const removeScheduleDay = (index) => {
    if (scheduleDays.length > 1) {
      const updatedSchedule = scheduleDays.filter((_, i) => i !== index);
      setScheduleDays(updatedSchedule);

      // Tính lại ngày kết thúc sau khi xóa buổi học
      setEditClassData((prev) => ({
        ...prev,
        course_end_date: calculateEndDate(
          prev.course_start_date,
          updatedSchedule,
          prev.total_sessions
        ),
      }));
    }
  };

  // Tính ngày kết thúc dựa trên ngày bắt đầu và lịch học
  const calculateEndDate = (startDate, schedule, totalSessions) => {
    if (!startDate || !schedule || schedule.length === 0 || !totalSessions) {
      return "";
    }

    try {
      // Chuyển ngày bắt đầu thành đối tượng Date
      const start = new Date(startDate);
      if (isNaN(start.getTime())) {
        return "";
      }

      // Tổng số tuần cần thiết = tổng số buổi học / số buổi học mỗi tuần
      const sessionsPerWeek = schedule.length;
      const weeksNeeded = Math.ceil(totalSessions / sessionsPerWeek);

      // Tạo mảng chứa các ngày trong tuần từ lịch học (0-6, với 0 là Chủ Nhật)
      const daysOfWeek = schedule.map((day) => day.day_of_week);

      // Sắp xếp các ngày trong tuần
      daysOfWeek.sort((a, b) => a - b);

      // Tìm ngày đầu tiên trong lịch học sau ngày bắt đầu
      let currentDate = new Date(start);

      // Đếm số buổi học đã xếp lịch
      let sessionCount = 0;

      // Lặp qua từng tuần
      for (let week = 0; week < weeksNeeded * 2; week++) {
        // Nhân 2 để đảm bảo đủ số buổi
        // Lặp qua các ngày trong tuần từ lịch học
        for (let dayIndex = 0; dayIndex < daysOfWeek.length; dayIndex++) {
          const targetDayOfWeek = daysOfWeek[dayIndex];

          // Tìm ngày tiếp theo có thứ phù hợp
          while (currentDate.getDay() !== targetDayOfWeek) {
            currentDate.setDate(currentDate.getDate() + 1);
          }

          sessionCount++;

          // Nếu đã đủ số buổi học, trả về ngày hiện tại
          if (sessionCount >= totalSessions) {
            return currentDate.toISOString().split("T")[0];
          }

          // Di chuyển đến ngày tiếp theo
          currentDate.setDate(currentDate.getDate() + 1);
        }
      }

      // Nếu không tính được, trả về chuỗi rỗng
      return "";
    } catch (error) {
      console.error("Lỗi khi tính ngày kết thúc:", error);
      return "";
    }
  };

  // Xử lý thay đổi ngày bắt đầu
  const handleStartDateChange = (e) => {
    const startDate = e.target.value;

    // Lấy thứ từ ngày bắt đầu
    const dayOfWeek = getDayOfWeekFromDate(startDate);

    // Cập nhật lịch học với thứ mới
    const updatedSchedule = [
      {
        day_of_week: dayOfWeek,
        start_period: 1,
        end_period: 2,
        start_time: "07:00",
        end_time: "08:40",
        room_id: editClassData.room_id,
        is_recurring: true,
        specific_dates: [],
        excluded_dates: [],
      },
    ];

    // Cập nhật lịch học
    setScheduleDays(updatedSchedule);

    // Cập nhật ngày bắt đầu và tính ngày kết thúc
    setEditClassData((prev) => ({
      ...prev,
      course_start_date: startDate,
      course_end_date: calculateEndDate(
        startDate,
        updatedSchedule,
        prev.total_sessions
      ),
    }));
  };

  // Xử lý thay đổi số buổi học
  const handleTotalSessionsChange = (e) => {
    const totalSessions = e.target.value;

    setEditClassData((prev) => ({
      ...prev,
      total_sessions: totalSessions,
      // Tính lại ngày kết thúc khi thay đổi số buổi học
      course_end_date: calculateEndDate(
        prev.course_start_date,
        scheduleDays,
        totalSessions
      ),
    }));
  };

  // Thêm phần render cảnh báo xung đột lịch
  const renderConflictWarnings = () => {
    if (conflictWarnings.length === 0) {
      return null;
    }

    return (
      <Alert severity="warning" sx={{ mt: 2, mb: 2 }}>
        <AlertTitle>Phát hiện xung đột lịch học</AlertTitle>
        <List dense>
          {conflictWarnings.map((conflict, index) => (
            <ListItem key={index}>
              <ListItemIcon>
                {conflict.type === "teacher" ? (
                  <Person color="error" />
                ) : (
                  <Room color="error" />
                )}
              </ListItemIcon>
              <ListItemText primary={conflict.message} />
            </ListItem>
          ))}
        </List>
      </Alert>
    );
  };

  if (isLoading) {
    return (
      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          height: "80vh",
        }}
      >
        <CircularProgress />
      </Box>
    );
  }

  if (!classInfo) {
    return (
      <Box sx={{ py: 3 }}>
        <Card sx={{ textAlign: "center", p: 4 }}>
          <Typography variant="h5" color="error" gutterBottom>
            Không tìm thấy lớp học
          </Typography>
          <Typography variant="body1" sx={{ mb: 3 }}>
            Lớp học bạn đang tìm kiếm không tồn tại hoặc đã bị xóa.
          </Typography>
          <Button
            variant="contained"
            color="primary"
            onClick={() => navigate("/teacher/classes")}
          >
            Quay lại danh sách lớp
          </Button>
        </Card>
      </Box>
    );
  }

  return (
    <Box sx={{ padding: 3 }}>
      <Box sx={{ mb: 3 }}>
        <Button
          variant="outlined"
          onClick={() => navigate("/teacher/classes")}
          sx={{ mb: 2 }}
        >
          Quay lại danh sách lớp
        </Button>
        <Box
          sx={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "flex-start",
          }}
        >
          <Box>
            <Typography variant="h4" gutterBottom>
              {classInfo.class_name}
            </Typography>
            <Typography variant="subtitle1" color="text.secondary" gutterBottom>
              {classInfo.subject_id?.name} ({classInfo.subject_id?.code})
            </Typography>
          </Box>
          <Button
            variant="contained"
            color="primary"
            startIcon={<Edit />}
            onClick={handleOpenEditClassDialog}
          >
            Chỉnh sửa thông tin lớp
          </Button>
        </Box>
      </Box>

      <Grid container spacing={3} sx={{ mb: 3 }}>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Thông tin lớp học
              </Typography>
              <Box sx={{ mt: 2 }}>
                <Typography
                  variant="body2"
                  sx={{ mb: 1, display: "flex", alignItems: "center" }}
                >
                  <CalendarToday sx={{ mr: 1, fontSize: "small" }} />
                  Học kỳ: {classInfo.semester_id?.name || "N/A"}
                </Typography>
                <Typography
                  variant="body2"
                  sx={{ mb: 1, display: "flex", alignItems: "center" }}
                >
                  <CalendarToday sx={{ mr: 1, fontSize: "small" }} />
                  Ngày bắt đầu:{" "}
                  {classInfo.course_start_date
                    ? new Date(classInfo.course_start_date).toLocaleDateString(
                        "vi-VN"
                      )
                    : "N/A"}
                </Typography>
                <Typography
                  variant="body2"
                  sx={{ mb: 1, display: "flex", alignItems: "center" }}
                >
                  <CalendarToday sx={{ mr: 1, fontSize: "small" }} />
                  Ngày kết thúc:{" "}
                  {classInfo.course_end_date
                    ? new Date(classInfo.course_end_date).toLocaleDateString(
                        "vi-VN"
                      )
                    : "N/A"}
                </Typography>
                <Typography
                  variant="body2"
                  sx={{ mb: 1, display: "flex", alignItems: "center" }}
                >
                  <Info sx={{ mr: 1, fontSize: "small" }} />
                  Số buổi học: {classInfo.total_sessions || 15}
                </Typography>
              </Box>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Điểm danh
              </Typography>
              <Box sx={{ mt: 2 }}>
                <Typography variant="body2" sx={{ mb: 1 }}>
                  Tổng số buổi: {attendanceStats.total}
                </Typography>
                <Typography variant="body2" sx={{ mb: 1 }}>
                  Đã hoàn thành: {attendanceStats.completed}
                </Typography>
                <Typography variant="body2" sx={{ mb: 1 }}>
                  Chưa hoàn thành: {attendanceStats.pending}
                </Typography>
              </Box>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Sinh viên
              </Typography>
              <Box sx={{ mt: 2 }}>
                <Typography variant="body2" sx={{ mb: 1 }}>
                  Tổng số sinh viên: {students.length}
                </Typography>
              </Box>
              <Button
                variant="outlined"
                size="small"
                startIcon={<Download />}
                onClick={handleExportStudentList}
                sx={{ mt: 2 }}
              >
                Xuất danh sách
              </Button>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      <Box sx={{ width: "100%" }}>
        <Box sx={{ borderBottom: 1, borderColor: "divider" }}>
          <Tabs
            value={tabValue}
            onChange={handleChangeTab}
            aria-label="lớp học tabs"
          >
            <Tab label="Sinh viên" {...a11yProps(0)} />
            <Tab label="Phiên điểm danh" {...a11yProps(1)} />
          </Tabs>
        </Box>
        <TabPanel value={tabValue} index={0}>
          <Box sx={{ display: "flex", justifyContent: "flex-end", mb: 2 }}>
            <Button
              variant="contained"
              startIcon={<PersonAdd />}
              onClick={handleOpenAddStudentDialog}
            >
              Thêm sinh viên
            </Button>
          </Box>
          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>STT</TableCell>
                  <TableCell>Mã sinh viên</TableCell>
                  <TableCell>Họ và tên</TableCell>
                  <TableCell>Email</TableCell>
                  <TableCell>Trạng thái</TableCell>
                  <TableCell>Thao tác</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {students.length > 0 ? (
                  students.map((student, index) => {
                    // Kiểm tra và log mỗi sinh viên để debug
                    // console.log(`Hiển thị sinh viên ${index + 1}:`, student);

                    // Kiểm tra có face data từ cả hai nguồn có thể
                    const hasFaceData =
                      student.has_face_data ||
                      !!(
                        student.faceFeatures &&
                        student.faceFeatures.descriptors &&
                        student.faceFeatures.descriptors.length > 0
                      );

                    return (
                      <TableRow key={student._id}>
                        <TableCell>{index + 1}</TableCell>
                        <TableCell>
                          {student.school_info?.student_id || "N/A"}
                        </TableCell>
                        <TableCell>
                          {student.full_name || student.name}
                        </TableCell>
                        <TableCell>{student.email}</TableCell>
                        <TableCell>
                          {hasFaceData ? (
                            <Chip
                              label="Đã đăng ký khuôn mặt"
                              color="success"
                              size="small"
                            />
                          ) : (
                            <Chip
                              label="Chưa đăng ký khuôn mặt"
                              color="warning"
                              size="small"
                            />
                          )}
                        </TableCell>
                        <TableCell>
                          <Tooltip title="Xóa khỏi lớp">
                            <IconButton
                              size="small"
                              color="error"
                              onClick={() => handleRemoveStudent(student._id)}
                            >
                              <Delete />
                            </IconButton>
                          </Tooltip>
                        </TableCell>
                      </TableRow>
                    );
                  })
                ) : (
                  <TableRow>
                    <TableCell colSpan={6} align="center">
                      Chưa có sinh viên nào trong lớp
                    </TableCell>
                  </TableRow>
                )}
              </TableBody>
            </Table>
          </TableContainer>
        </TabPanel>
        <TabPanel value={tabValue} index={1}>
          <Box sx={{ display: "flex", justifyContent: "flex-end", mb: 2 }}>
            <Button
              variant="contained"
              startIcon={<Add />}
              onClick={handleOpenSessionDialog}
            >
              Tạo phiên điểm danh
            </Button>
          </Box>
          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>STT</TableCell>
                  <TableCell>Ngày</TableCell>
                  <TableCell>Tiết/Thời gian</TableCell>
                  <TableCell>Phòng</TableCell>
                  <TableCell>Ghi chú</TableCell>
                  <TableCell>Trạng thái</TableCell>
                  <TableCell>Thao tác</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {attendanceSessions.length > 0 ? (
                  attendanceSessions.map((session, index) => {
                    // Log the session.room before rendering
                    // console.log(
                    //   `[DEBUG] Rendering room for session ${session._id}:`,
                    //   session.room
                    // );
                    return (
                      <TableRow key={session._id}>
                        <TableCell>{index + 1}</TableCell>
                        <TableCell>
                          {new Date(session.date).toLocaleString("vi-VN", {
                            weekday: "long",
                            year: "numeric",
                            month: "numeric",
                            day: "numeric",
                          })}
                        </TableCell>
                        <TableCell>
                          {session.start_period && session.end_period ? (
                            <Box>
                              <Typography variant="body2">
                                Tiết {session.start_period}-{session.end_period}
                              </Typography>
                              <Typography
                                variant="caption"
                                color="text.secondary"
                              >
                                {(() => {
                                  // Hiển thị thời gian bắt đầu từ API
                                  const startTime = session.start_time
                                    ? new Date(
                                        session.start_time
                                      ).toLocaleTimeString("vi-VN", {
                                        hour: "2-digit",
                                        minute: "2-digit",
                                      })
                                    : "N/A";

                                  // Tính thời gian kết thúc dự kiến dựa trên lịch học, không phải thời gian kết thúc thực tế
                                  // Mỗi tiết thường kéo dài 50 phút
                                  let endTimeDate;
                                  if (session.start_time) {
                                    // Tính thời gian kết thúc dự kiến dựa trên số tiết
                                    const tietsCount =
                                      session.end_period -
                                      session.start_period +
                                      1;
                                    endTimeDate = new Date(session.start_time);
                                    endTimeDate.setMinutes(
                                      endTimeDate.getMinutes() + tietsCount * 50
                                    );
                                  }

                                  const endTime = endTimeDate
                                    ? endTimeDate.toLocaleTimeString("vi-VN", {
                                        hour: "2-digit",
                                        minute: "2-digit",
                                      })
                                    : "N/A";

                                  return `${startTime} - ${endTime}`;
                                })()}
                              </Typography>
                            </Box>
                          ) : session.start_time ? (
                            (() => {
                              // Hiển thị thời gian bắt đầu từ API
                              const startTime = new Date(
                                session.start_time
                              ).toLocaleTimeString("vi-VN", {
                                hour: "2-digit",
                                minute: "2-digit",
                              });

                              // Mặc định coi mỗi buổi học kéo dài 4 tiết (200 phút) nếu không có thông tin tiết
                              const endTimeDate = new Date(session.start_time);
                              endTimeDate.setMinutes(
                                endTimeDate.getMinutes() + 200
                              );
                              const endTime = endTimeDate.toLocaleTimeString(
                                "vi-VN",
                                { hour: "2-digit", minute: "2-digit" }
                              );

                              return `${startTime} - ${endTime}`;
                            })()
                          ) : (
                            "N/A"
                          )}
                        </TableCell>
                        <TableCell>
                          {session.room ? (
                            <Tooltip title="Thông tin phòng học">
                              <Typography variant="body2">
                                {session.room.room_number || "Không xác định"} -{" "}
                                {session.room.building_id?.name ||
                                  "Không xác định"}{" "}
                                -{" "}
                                {session.room.building_id?.campus_id?.name ||
                                  "Không xác định"}
                              </Typography>
                            </Tooltip>
                          ) : (
                            <Typography variant="body2" color="text.secondary">
                              Không có phòng
                            </Typography>
                          )}
                        </TableCell>
                        <TableCell>
                          {session.notes ? (
                            <Tooltip title={session.notes}>
                              <Typography
                                variant="body2"
                                sx={{
                                  maxWidth: "150px",
                                  whiteSpace: "nowrap",
                                  overflow: "hidden",
                                  textOverflow: "ellipsis",
                                }}
                              >
                                {session.notes}
                              </Typography>
                            </Tooltip>
                          ) : (
                            <Typography variant="body2" color="text.secondary">
                              -
                            </Typography>
                          )}
                        </TableCell>
                        <TableCell>
                          {session.status === "completed" ? (
                            <Chip
                              label="Hoàn thành"
                              color="success"
                              size="small"
                            />
                          ) : session.status === "in_progress" ? (
                            <Chip
                              label="Đang diễn ra"
                              color="primary"
                              size="small"
                            />
                          ) : (
                            <Chip
                              label="Chưa bắt đầu"
                              color="default"
                              size="small"
                            />
                          )}
                        </TableCell>
                        <TableCell>
                          <Box sx={{ display: "flex", gap: 1 }}>
                            <Button
                              variant="contained"
                              size="small"
                              color="primary"
                              onClick={() => handleStartSession(session._id)}
                              startIcon={
                                session.status === "completed" ? (
                                  <CheckCircle />
                                ) : (
                                  <PlayArrow />
                                )
                              }
                            >
                              {session.status === "completed"
                                ? "Xem"
                                : "Bắt đầu"}
                            </Button>
                            <Tooltip title="Chỉnh sửa thông tin">
                              <IconButton
                                size="small"
                                color="default"
                                onClick={() => {
                                  setSessionFormData({
                                    ...session,
                                    date: session.date.split("T")[0],
                                  });
                                  // Tải danh sách phòng học
                                  fetchRooms();
                                  setOpenSessionDialog(true);
                                }}
                              >
                                <Edit />
                              </IconButton>
                            </Tooltip>
                          </Box>
                        </TableCell>
                      </TableRow>
                    );
                  })
                ) : (
                  <TableRow>
                    <TableCell colSpan={8} align="center">
                      Chưa có phiên điểm danh nào
                    </TableCell>
                  </TableRow>
                )}
              </TableBody>
            </Table>
          </TableContainer>
        </TabPanel>
      </Box>

      {/* Dialog tạo/chỉnh sửa phiên điểm danh */}
      <Dialog
        open={openSessionDialog}
        onClose={handleCloseSessionDialog}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>
          {sessionFormData._id
            ? "Chỉnh sửa phiên điểm danh"
            : "Tạo phiên điểm danh mới"}
        </DialogTitle>
        <DialogContent>
          <Box sx={{ mt: 2 }}>
            <TextField
              fullWidth
              label="Ngày"
              name="date"
              type="date"
              value={sessionFormData.date}
              onChange={handleSessionFormChange}
              sx={{ mb: 2 }}
            />

            <Grid container spacing={2} sx={{ mb: 2 }}>
              <Grid item xs={6} md={3}>
                <TextField
                  fullWidth
                  label="Tiết bắt đầu"
                  name="start_period"
                  type="number"
                  value={sessionFormData.start_period}
                  onChange={handleSessionFormChange}
                  InputProps={{ inputProps: { min: 1, max: 15 } }}
                />
              </Grid>
              <Grid item xs={6} md={3}>
                <TextField
                  fullWidth
                  label="Tiết kết thúc"
                  name="end_period"
                  type="number"
                  value={sessionFormData.end_period}
                  onChange={handleSessionFormChange}
                  InputProps={{
                    inputProps: {
                      min: sessionFormData.start_period || 1,
                      max: 15,
                    },
                  }}
                />
              </Grid>
              <Grid item xs={6} md={3}>
                <TextField
                  fullWidth
                  label="Thời gian bắt đầu"
                  name="start_time"
                  type="time"
                  value={sessionFormData.start_time}
                  onChange={handleSessionFormChange}
                  InputLabelProps={{ shrink: true }}
                />
              </Grid>
              <Grid item xs={6} md={3}>
                <TextField
                  fullWidth
                  label="Thời gian kết thúc"
                  name="end_time"
                  type="time"
                  value={sessionFormData.end_time}
                  onChange={handleSessionFormChange}
                  InputLabelProps={{ shrink: true }}
                />
              </Grid>
            </Grid>

            <Grid container spacing={2} sx={{ mb: 2 }}>
              <Grid item xs={12}>
                <FormControl fullWidth>
                  <InputLabel>Phòng học</InputLabel>
                  <Select
                    name="room"
                    value={sessionFormData.room || ""}
                    onChange={handleSessionFormChange}
                    label="Phòng học"
                  >
                    <MenuItem value="">-- Chọn phòng học --</MenuItem>
                    {rooms &&
                      rooms.map((room) => (
                        <MenuItem key={room._id} value={room._id}>
                          {room.room_number || "Không xác định"} -{" "}
                          {room.building_id?.name || "Không xác định"} -{" "}
                          {room.building_id?.campus_id?.name ||
                            "Không xác định"}
                        </MenuItem>
                      ))}
                  </Select>
                  <FormHelperText>
                    Chọn phòng học với thông tin đầy đủ
                  </FormHelperText>
                </FormControl>
              </Grid>
            </Grid>

            <TextField
              fullWidth
              label="Ghi chú"
              name="notes"
              multiline
              rows={3}
              placeholder="Nội dung buổi học, yêu cầu đặc biệt, v.v..."
              value={sessionFormData.notes}
              onChange={handleSessionFormChange}
              sx={{ mb: 2 }}
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseSessionDialog}>Hủy</Button>
          <Button
            variant="contained"
            color="primary"
            onClick={
              sessionFormData._id ? handleUpdateSession : handleCreateSession
            }
          >
            {sessionFormData._id ? "Lưu thay đổi" : "Tạo"}
          </Button>
        </DialogActions>
      </Dialog>

      {/* Dialog thêm sinh viên */}
      <Dialog
        open={openAddStudentDialog}
        onClose={handleCloseAddStudentDialog}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>Thêm sinh viên vào lớp</DialogTitle>
        <DialogContent>
          <Box sx={{ mb: 3, mt: 1 }}>
            <FormControl fullWidth sx={{ mb: 2 }}>
              <InputLabel>Chọn lớp chính</InputLabel>
              <Select
                value={selectedMainClass}
                onChange={handleMainClassChange}
                label="Chọn lớp chính"
                disabled={isLoadingStudents}
              >
                <MenuItem value="">-- Chọn lớp chính --</MenuItem>
                {mainClasses.map((mainClass) => (
                  <MenuItem key={mainClass._id} value={mainClass._id}>
                    {mainClass.name} ({mainClass.class_code})
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            {selectedMainClass && (
              <TextField
                fullWidth
                label="Tìm kiếm sinh viên"
                value={searchStudent}
                onChange={(e) => setSearchStudent(e.target.value)}
                disabled={isLoadingStudents}
              />
            )}
          </Box>

          {isLoadingStudents ? (
            <Box sx={{ display: "flex", justifyContent: "center", my: 3 }}>
              <CircularProgress />
            </Box>
          ) : (
            <Box sx={{ maxHeight: 400, overflow: "auto", mb: 2 }}>
              {!selectedMainClass ? (
                <Alert severity="info">
                  Vui lòng chọn lớp chính để xem danh sách sinh viên
                </Alert>
              ) : availableStudents.length === 0 ? (
                <Alert severity="warning">
                  Không tìm thấy sinh viên có thể thêm vào lớp
                </Alert>
              ) : (
                <List>
                  {availableStudents
                    .filter(
                      (student) =>
                        student.full_name
                          ?.toLowerCase()
                          .includes(searchStudent.toLowerCase()) ||
                        student.school_info?.student_id
                          ?.toLowerCase()
                          .includes(searchStudent.toLowerCase()) ||
                        student.email
                          ?.toLowerCase()
                          .includes(searchStudent.toLowerCase())
                    )
                    .map((student) => (
                      <React.Fragment key={student._id}>
                        <ListItem>
                          <ListItemText
                            primary={student.full_name || student.name}
                            secondary={
                              <>
                                {student.school_info?.student_id ||
                                  "Chưa có mã SV"}{" "}
                                - {student.email}
                              </>
                            }
                          />
                          <ListItemSecondaryAction>
                            <Checkbox
                              edge="end"
                              checked={selectedStudents.includes(student._id)}
                              onChange={() => handleSelectStudent(student._id)}
                            />
                          </ListItemSecondaryAction>
                        </ListItem>
                        <Divider />
                      </React.Fragment>
                    ))}
                </List>
              )}
            </Box>
          )}

          <Box sx={{ textAlign: "right" }}>
            <Typography variant="body2">
              Đã chọn: {selectedStudents.length} sinh viên
            </Typography>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseAddStudentDialog}>Hủy</Button>
          <Button
            variant="contained"
            color="primary"
            onClick={handleAddStudents}
            disabled={selectedStudents.length === 0 || isLoadingStudents}
          >
            Thêm vào lớp
          </Button>
        </DialogActions>
      </Dialog>

      {/* Dialog chỉnh sửa lớp học */}
      <Dialog
        open={openEditClassDialog}
        onClose={handleCloseEditClassDialog}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle
          sx={{ bgcolor: "primary.main", color: "white", fontWeight: "bold" }}
        >
          <Edit sx={{ verticalAlign: "middle", mr: 1 }} />
          Chỉnh sửa thông tin lớp học
        </DialogTitle>
        <DialogContent dividers>
          {editClassData && (
            <Grid container spacing={2} sx={{ mt: 1 }}>
              <Grid item xs={12} sm={6}>
                <TextField
                  fullWidth
                  label="Tên lớp"
                  name="class_name"
                  value={editClassData.class_name}
                  onChange={handleEditClassChange}
                  required
                  variant="outlined"
                  placeholder="VD: Lập trình web - Nhóm 1"
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  fullWidth
                  label="Mã lớp"
                  name="class_code"
                  value={editClassData.class_code}
                  onChange={handleEditClassChange}
                  required
                  variant="outlined"
                  placeholder="VD: WEB101-G1"
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <FormControl fullWidth required>
                  <InputLabel>Môn học</InputLabel>
                  <Select
                    label="Môn học"
                    name="subject_id"
                    value={editClassData.subject_id}
                    onChange={handleEditClassChange}
                  >
                    {subjects.map((subject) => (
                      <MenuItem key={subject._id} value={subject._id}>
                        {subject.name} ({subject.code})
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12} sm={6}>
                <FormControl fullWidth required>
                  <InputLabel>Học kỳ</InputLabel>
                  <Select
                    label="Học kỳ"
                    name="semester_id"
                    value={editClassData.semester_id}
                    onChange={handleEditClassChange}
                  >
                    {semesters.map((sem) => (
                      <MenuItem key={sem._id} value={sem._id}>
                        {sem.name} ({sem.academic_year})
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <InputLabel>Lớp chính</InputLabel>
                  <Select
                    label="Lớp chính"
                    name="main_class_id"
                    value={editClassData.main_class_id}
                    onChange={handleEditClassChange}
                  >
                    <MenuItem value="">Không chọn lớp chính</MenuItem>
                    {mainClasses.map((mainClass) => (
                      <MenuItem key={mainClass._id} value={mainClass._id}>
                        {mainClass.name} ({mainClass.class_code})
                      </MenuItem>
                    ))}
                  </Select>
                  <FormHelperText>
                    Có thể để trống nếu lớp dành cho nhiều lớp chính
                  </FormHelperText>
                </FormControl>
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  fullWidth
                  type="number"
                  label="Tổng số buổi học"
                  name="total_sessions"
                  value={editClassData.total_sessions}
                  onChange={handleTotalSessionsChange}
                  required
                  variant="outlined"
                  InputProps={{ inputProps: { min: 1 } }}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  fullWidth
                  type="date"
                  label="Ngày bắt đầu khóa học"
                  name="course_start_date"
                  value={editClassData.course_start_date}
                  onChange={handleStartDateChange}
                  required
                  variant="outlined"
                  InputLabelProps={{ shrink: true }}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  fullWidth
                  type="date"
                  label="Ngày kết thúc khóa học"
                  name="course_end_date"
                  value={editClassData.course_end_date}
                  onChange={handleEditClassChange}
                  variant="outlined"
                  InputLabelProps={{ shrink: true }}
                />
              </Grid>

              {/* Chọn phòng học */}
              <Grid item xs={12}>
                <Typography
                  variant="subtitle1"
                  gutterBottom
                  sx={{ fontWeight: "bold", mt: 2 }}
                >
                  <Room sx={{ verticalAlign: "middle", mr: 1 }} />
                  Thông tin phòng học
                </Typography>
                <Divider sx={{ mb: 2 }} />
              </Grid>
              <Grid item xs={12} sm={4}>
                <FormControl fullWidth>
                  <InputLabel>Cơ sở</InputLabel>
                  <Select
                    label="Cơ sở"
                    value={selectedCampus}
                    onChange={handleCampusChange}
                  >
                    <MenuItem value="">Chọn cơ sở</MenuItem>
                    {campuses.map((campus) => (
                      <MenuItem key={campus._id} value={campus._id}>
                        {campus.name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12} sm={4}>
                <FormControl fullWidth disabled={!selectedCampus}>
                  <InputLabel>Tòa nhà</InputLabel>
                  <Select
                    label="Tòa nhà"
                    value={selectedBuilding}
                    onChange={handleBuildingChange}
                  >
                    <MenuItem value="">Chọn tòa nhà</MenuItem>
                    {buildings.map((building) => (
                      <MenuItem key={building._id} value={building._id}>
                        {building.name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12} sm={4}>
                <FormControl fullWidth disabled={!selectedBuilding}>
                  <InputLabel>Phòng học</InputLabel>
                  <Select
                    label="Phòng học"
                    name="room_id"
                    value={editClassData.room_id}
                    onChange={handleEditClassChange}
                  >
                    <MenuItem value="">Chọn phòng học</MenuItem>
                    {rooms.map((room) => (
                      <MenuItem key={room._id} value={room._id}>
                        {room.room_number} (Sức chứa: {room.capacity})
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>

              {/* Lịch học */}
              <Grid item xs={12}>
                <Typography
                  variant="subtitle1"
                  gutterBottom
                  sx={{ fontWeight: "bold", mt: 2 }}
                >
                  <Event sx={{ verticalAlign: "middle", mr: 1 }} />
                  Lịch học
                </Typography>
                <Divider sx={{ mb: 2 }} />
              </Grid>

              {scheduleDays.map((day, index) => (
                <Grid
                  container
                  spacing={2}
                  key={index}
                  sx={{
                    ml: 0,
                    mt: 1,
                    width: "100%",
                    pb: 2,
                    borderBottom:
                      index < scheduleDays.length - 1
                        ? "1px dashed #ddd"
                        : "none",
                  }}
                >
                  <Grid item xs={12} sm={3}>
                    <TextField
                      fullWidth
                      label="Thứ trong tuần"
                      value={getDayOfWeekName(day.day_of_week)}
                      InputProps={{
                        readOnly: true,
                      }}
                      variant="outlined"
                      helperText="Được xác định từ ngày bắt đầu"
                    />
                  </Grid>
                  <Grid item xs={6} sm={2}>
                    <TextField
                      fullWidth
                      label="Tiết bắt đầu"
                      type="number"
                      value={day.start_period}
                      onChange={(e) =>
                        handleScheduleChange(
                          index,
                          "start_period",
                          parseInt(e.target.value)
                        )
                      }
                      InputProps={{ inputProps: { min: 1, max: 15 } }}
                    />
                  </Grid>
                  <Grid item xs={6} sm={2}>
                    <TextField
                      fullWidth
                      label="Tiết kết thúc"
                      type="number"
                      value={day.end_period}
                      onChange={(e) =>
                        handleScheduleChange(
                          index,
                          "end_period",
                          parseInt(e.target.value)
                        )
                      }
                      InputProps={{
                        inputProps: { min: day.start_period, max: 15 },
                      }}
                    />
                  </Grid>
                  <Grid item xs={6} sm={2}>
                    <TextField
                      fullWidth
                      label="Giờ bắt đầu"
                      type="time"
                      value={day.start_time}
                      onChange={(e) =>
                        handleScheduleChange(
                          index,
                          "start_time",
                          e.target.value
                        )
                      }
                      InputLabelProps={{ shrink: true }}
                    />
                  </Grid>
                  <Grid item xs={6} sm={2}>
                    <TextField
                      fullWidth
                      label="Giờ kết thúc"
                      type="time"
                      value={day.end_time}
                      onChange={(e) =>
                        handleScheduleChange(index, "end_time", e.target.value)
                      }
                      InputLabelProps={{ shrink: true }}
                    />
                  </Grid>
                  <Grid
                    item
                    xs={12}
                    sm={1}
                    sx={{ display: "flex", alignItems: "center" }}
                  >
                    <IconButton
                      color="error"
                      onClick={() => removeScheduleDay(index)}
                      disabled={scheduleDays.length <= 1}
                    >
                      <Chip
                        label="Xóa"
                        color="error"
                        size="small"
                        variant="outlined"
                        disabled={scheduleDays.length <= 1}
                      />
                    </IconButton>
                  </Grid>

                  <Grid item xs={12} sm={4}>
                    <FormControl fullWidth>
                      <InputLabel>Phòng học riêng</InputLabel>
                      <Select
                        label="Phòng học riêng"
                        value={day.room_id || ""}
                        onChange={(e) =>
                          handleScheduleChange(index, "room_id", e.target.value)
                        }
                      >
                        <MenuItem value="">Dùng phòng mặc định</MenuItem>
                        {rooms.map((room) => (
                          <MenuItem key={room._id} value={room._id}>
                            {room.room_number} (Sức chứa: {room.capacity})
                          </MenuItem>
                        ))}
                      </Select>
                      <FormHelperText>
                        Phòng học riêng cho buổi này, để trống sẽ dùng phòng mặc
                        định của lớp
                      </FormHelperText>
                    </FormControl>
                  </Grid>

                  <Grid item xs={12} sm={4}>
                    <FormControl fullWidth>
                      <InputLabel>Loại lịch</InputLabel>
                      <Select
                        label="Loại lịch"
                        value={day.is_recurring}
                        onChange={(e) =>
                          handleScheduleChange(
                            index,
                            "is_recurring",
                            e.target.value
                          )
                        }
                      >
                        <MenuItem value={true}>Định kỳ hàng tuần</MenuItem>
                        <MenuItem value={false}>Các ngày cụ thể</MenuItem>
                      </Select>
                    </FormControl>
                  </Grid>
                </Grid>
              ))}

              <Grid item xs={12}>
                <Button
                  variant="outlined"
                  color="primary"
                  startIcon={<Add />}
                  onClick={addScheduleDay}
                  sx={{ mt: 1 }}
                >
                  Thêm lịch học
                </Button>
              </Grid>
            </Grid>
          )}
        </DialogContent>
        <DialogActions sx={{ px: 3, pb: 2 }}>
          <Button
            onClick={handleCloseEditClassDialog}
            disabled={isSubmittingEdit}
          >
            Hủy
          </Button>
          <Button
            onClick={handleUpdateClass}
            variant="contained"
            color="primary"
            disabled={isSubmittingEdit}
            startIcon={
              isSubmittingEdit ? <CircularProgress size={20} /> : <Edit />
            }
          >
            {isSubmittingEdit ? "Đang cập nhật..." : "Cập nhật lớp học"}
          </Button>
        </DialogActions>
      </Dialog>

      {/* Thêm component hiển thị cảnh báo xung đột ngay trước các nút hành động */}
      {editMode && renderConflictWarnings()}

      {/* Các nút hành động */}
      <Box sx={{ mt: 3, display: "flex", justifyContent: "flex-end", gap: 2 }}>
        {editMode ? (
          <>
            <Button
              variant="outlined"
              onClick={() => {
                setEditMode(false);
                setEditClassData({ ...classInfo });
                setScheduleDays(
                  classInfo.schedule?.map((item) => ({ ...item })) || []
                );
                setConflictWarnings([]); // Xóa cảnh báo khi hủy
              }}
              disabled={isUpdating || checkingConflicts}
            >
              Hủy
            </Button>
            <Button
              variant="contained"
              onClick={handleUpdateClass}
              disabled={isUpdating || checkingConflicts}
              startIcon={
                isUpdating || checkingConflicts ? (
                  <CircularProgress size={20} />
                ) : null
              }
            >
              {isUpdating
                ? "Đang cập nhật..."
                : checkingConflicts
                ? "Đang kiểm tra..."
                : "Lưu thay đổi"}
            </Button>
          </>
        ) : (
          <Button
            variant="contained"
            onClick={() => setEditMode(true)}
            disabled={isLoading}
          >
            Chỉnh sửa lớp học
          </Button>
        )}
      </Box>
    </Box>
  );
};

export default TeacherClassDetailPage;
