import React, { useState, useEffect, useCallback } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useSnackbar } from "notistack";
import {
  Box,
  Typography,
  Tabs,
  Tab,
  Paper,
  Button,
  TextField,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TablePagination,
  IconButton,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  Grid,
  Card,
  CardContent,
  Chip,
  Tooltip,
  CircularProgress,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  InputAdornment,
  Divider,
} from "@mui/material";
import {
  Add,
  Delete,
  Edit,
  Search,
  Refresh,
  Room,
  Domain,
  ArrowBack,
  LocationOn,
  Business,
} from "@mui/icons-material";
import {
  fetchAllCampuses,
  createCampus,
  updateCampus,
  deleteCampus,
  fetchAllBuildings,
  createBuilding,
  updateBuilding,
  deleteBuilding,
  fetchAllRooms,
  createRoom,
  updateRoom,
  deleteRoom,
  setSelectedCampus,
  setSelectedBuilding,
} from "../../redux/slices/facilitySlice";
import { store } from "../../redux/store";
import { useNavigate } from "react-router-dom";
import { toast } from "react-hot-toast";
import axiosInstance from "../../services/axiosInstance";

const FacilitiesPage = () => {
  const dispatch = useDispatch();
  const { enqueueSnackbar } = useSnackbar();
  const navigate = useNavigate();

  // Lấy trạng thái từ Redux
  const {
    campuses,
    buildings,
    rooms,
    selectedCampus,
    selectedBuilding,
    isLoading,
    error,
    campusPagination,
    buildingPagination,
    roomPagination,
  } = useSelector((state) => state.facility);

  // States cho tabs
  const [tabValue, setTabValue] = useState(0);

  // State cho form submission
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Quản lý cơ sở (Campuses)
  const [localCampusList, setLocalCampusList] = useState([]);
  const [campusSearchTerm, setCampusSearchTerm] = useState("");
  const [campusPage, setCampusPage] = useState(0);
  const [campusRowsPerPage, setCampusRowsPerPage] = useState(10);

  // Quản lý tòa nhà (Buildings)
  const [buildingSearchTerm, setBuildingSearchTerm] = useState("");
  const [buildingPage, setBuildingPage] = useState(0);
  const [buildingRowsPerPage, setBuildingRowsPerPage] = useState(10);

  // Quản lý phòng học (Rooms)
  const [roomSearchTerm, setRoomSearchTerm] = useState("");
  const [roomPage, setRoomPage] = useState(0);
  const [roomRowsPerPage, setRoomRowsPerPage] = useState(10);

  // States cho dialogs cơ sở
  const [campusDialogOpen, setCampusDialogOpen] = useState(false);
  const [campusDialogMode, setCampusDialogMode] = useState("create");
  const [campusFormData, setCampusFormData] = useState({
    name: "",
    code: "",
    address: "",
    description: "",
    status: "active",
    image_url: "",
  });

  // States cho dialogs tòa nhà
  const [buildingDialogOpen, setBuildingDialogOpen] = useState(false);
  const [buildingDialogMode, setBuildingDialogMode] = useState("create");
  const [buildingFormData, setBuildingFormData] = useState({
    name: "",
    code: "",
    campus_id: "",
    floors_count: 1,
    year_built: new Date().getFullYear(),
    status: "active",
    facilities: [],
    image_url: "",
  });

  // States cho dialogs phòng học
  const [roomDialogOpen, setRoomDialogOpen] = useState(false);
  const [roomDialogMode, setRoomDialogMode] = useState("create");
  const [roomFormData, setRoomFormData] = useState({
    name: "",
    code: "",
    building_id: "",
    floor: 1,
    capacity: 30,
    equipment: "",
  });

  // States cho dialogs xóa
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [deleteDialogType, setDeleteDialogType] = useState("building");
  const [itemToDelete, setItemToDelete] = useState(null);

  // Fetch data khi component mount
  useEffect(() => {
    loadCampuses();
    loadBuildings();
    loadRooms();
  }, [dispatch]);

  // Fetch campuses với phân trang và tìm kiếm
  const loadCampuses = useCallback(() => {
    dispatch(
      fetchAllCampuses({
        page: campusPage + 1,
        limit: campusRowsPerPage,
        search: campusSearchTerm,
      })
    );
  }, [dispatch, campusPage, campusRowsPerPage, campusSearchTerm]);

  // Fetch buildings với phân trang và tìm kiếm
  const loadBuildings = useCallback(() => {
    dispatch(
      fetchAllBuildings({
        page: buildingPage + 1,
        limit: buildingRowsPerPage,
        search: buildingSearchTerm,
      })
    );
  }, [dispatch, buildingPage, buildingRowsPerPage, buildingSearchTerm]);

  // Fetch rooms với phân trang và tìm kiếm
  const loadRooms = useCallback(() => {
    dispatch(
      fetchAllRooms({
        page: roomPage + 1,
        limit: roomRowsPerPage,
        search: roomSearchTerm,
        buildingId: selectedBuilding?._id || "",
      })
    );
  }, [dispatch, roomPage, roomRowsPerPage, roomSearchTerm, selectedBuilding]);

  // Chạy loadCampuses khi tham số thay đổi
  useEffect(() => {
    loadCampuses();
  }, [loadCampuses, campusPage, campusRowsPerPage, campusSearchTerm]);

  // Chạy loadBuildings khi tham số thay đổi
  useEffect(() => {
    loadBuildings();
  }, [loadBuildings, buildingPage, buildingRowsPerPage, buildingSearchTerm]);

  // Chạy loadRooms khi tham số thay đổi
  useEffect(() => {
    loadRooms();
  }, [loadRooms, roomPage, roomRowsPerPage, roomSearchTerm, selectedBuilding]);

  // Fetch danh sách campuses cho dropdown
  const fetchCampusList = useCallback(async () => {
    try {
      const response = await axiosInstance.get(
        "/facilities/campuses?limit=100"
      );
      setLocalCampusList(response.data.data);
    } catch (error) {
      console.error("Lỗi khi tải danh sách cơ sở:", error);
      toast.error("Không thể tải danh sách cơ sở cho dropdown");
    }
  }, []);

  // Chạy fetchCampusList khi component mount
  useEffect(() => {
    fetchCampusList();
  }, [fetchCampusList]);

  // Handlers cho tabs
  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  // Handlers cho tìm kiếm
  const handleCampusSearchChange = (event) => {
    setCampusSearchTerm(event.target.value);
  };

  const handleBuildingSearchChange = (event) => {
    setBuildingSearchTerm(event.target.value);
  };

  const handleRoomSearchChange = (event) => {
    setRoomSearchTerm(event.target.value);
  };

  // Handlers cho phân trang cơ sở
  const handleCampusChangePage = (event, newPage) => {
    setCampusPage(newPage);
  };

  const handleCampusChangeRowsPerPage = (event) => {
    setCampusRowsPerPage(parseInt(event.target.value, 10));
    setCampusPage(0);
  };

  // Handlers cho phân trang tòa nhà
  const handleBuildingChangePage = (event, newPage) => {
    setBuildingPage(newPage);
  };

  const handleBuildingChangeRowsPerPage = (event) => {
    setBuildingRowsPerPage(parseInt(event.target.value, 10));
    setBuildingPage(0);
  };

  // Handlers cho phân trang phòng học
  const handleRoomChangePage = (event, newPage) => {
    setRoomPage(newPage);
  };

  const handleRoomChangeRowsPerPage = (event) => {
    setRoomRowsPerPage(parseInt(event.target.value, 10));
    setRoomPage(0);
  };

  // Handlers cho bật/tắt campus dialog
  const handleOpenCampusDialog = (mode, campus = null) => {
    setCampusDialogMode(mode);
    if (mode === "edit" && campus) {
      setCampusFormData({
        _id: campus._id,
        name: campus.name,
        code: campus.code,
        address: campus.address,
        status: campus.status || "active",
      });
    } else {
      setCampusFormData({
        name: "",
        code: "",
        address: "",
        status: "active",
      });
    }
    setCampusDialogOpen(true);
  };

  const handleCloseCampusDialog = () => {
    setCampusDialogOpen(false);
  };

  // Handler cho campus form change
  const handleCampusFormChange = (e) => {
    const { name, value } = e.target;
    setCampusFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Handler cho submit campus form
  const handleCampusFormSubmit = async (e) => {
    e.preventDefault();

    const campusData = {
      ...campusFormData,
      name: campusFormData.name.trim(),
      code: campusFormData.code.trim(),
      address: campusFormData.address.trim(),
    };

    try {
      if (campusDialogMode === "add") {
        await dispatch(createCampus(campusData)).unwrap();
        toast.success("Thêm cơ sở thành công");
      } else {
        await dispatch(
          updateCampus({
            id: campusFormData._id,
            data: campusData,
          })
        ).unwrap();
        toast.success("Cập nhật cơ sở thành công");
      }

      handleCloseCampusDialog();
      loadCampuses();
      fetchCampusList(); // Cập nhật lại dropdown
    } catch (error) {
      if (error.message === "jwt expired") {
        toast.error("Phiên đăng nhập hết hạn, vui lòng đăng nhập lại");
      } else {
        toast.error(error.message || "Có lỗi xảy ra khi xử lý thông tin cơ sở");
      }
    }
  };

  // Handler cho xóa campus
  const handleDeleteCampus = async (campusId) => {
    try {
      await dispatch(deleteCampus(campusId)).unwrap();
      toast.success("Xóa cơ sở thành công");
      loadCampuses();
      fetchCampusList(); // Cập nhật lại dropdown
    } catch (error) {
      if (error.message === "jwt expired") {
        toast.error("Phiên đăng nhập hết hạn, vui lòng đăng nhập lại");
      } else {
        toast.error(error.message || "Có lỗi xảy ra khi xóa cơ sở");
      }
    }
  };

  // Handlers cho bật/tắt building dialog
  const handleOpenBuildingDialog = (mode, building = null) => {
    setBuildingDialogMode(mode);
    if (mode === "edit" && building) {
      setBuildingFormData({
        _id: building._id,
        name: building.name,
        code: building.code,
        campus_id: building.campus_id || "",
        floors_count: building.floors_count || 1,
        year_built: building.year_built || new Date().getFullYear(),
        status: building.status || "active",
      });
    } else {
      setBuildingFormData({
        name: "",
        code: "",
        campus_id: "",
        floors_count: 1,
        year_built: new Date().getFullYear(),
        status: "active",
      });
    }
    setBuildingDialogOpen(true);
  };

  const handleCloseBuildingDialog = () => {
    setBuildingDialogOpen(false);
  };

  // Handler cho building form change
  const handleBuildingFormChange = (e) => {
    const { name, value } = e.target;
    setBuildingFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Handler cho submit building form
  const handleBuildingFormSubmit = async (e) => {
    e.preventDefault();

    // Validate form data
    if (!buildingFormData.name.trim() || !buildingFormData.code.trim()) {
      toast.error("Vui lòng nhập đầy đủ tên và mã tòa nhà");
      return;
    }

    // Kiểm tra campus_id
    if (!buildingFormData.campus_id) {
      toast.error("Vui lòng chọn cơ sở cho tòa nhà");
      return;
    }

    const buildingData = {
      ...buildingFormData,
      name: buildingFormData.name.trim(),
      code: buildingFormData.code.trim(),
      floors_count: Number(buildingFormData.floors_count),
      year_built: Number(buildingFormData.year_built),
    };

    try {
      if (buildingDialogMode === "add") {
        await dispatch(createBuilding(buildingData)).unwrap();
        toast.success("Thêm tòa nhà thành công");
      } else {
        await dispatch(
          updateBuilding({
            id: buildingFormData._id,
            data: buildingData,
          })
        ).unwrap();
        toast.success("Cập nhật tòa nhà thành công");
      }

      handleCloseBuildingDialog();
      loadBuildings();
    } catch (error) {
      if (error.message === "jwt expired") {
        toast.error("Phiên đăng nhập hết hạn, vui lòng đăng nhập lại");
      } else {
        toast.error(
          error.message || "Có lỗi xảy ra khi xử lý thông tin tòa nhà"
        );
      }
    }
  };

  // Handler cho xóa building
  const handleDeleteBuilding = async (buildingId) => {
    try {
      await dispatch(deleteBuilding(buildingId)).unwrap();
      toast.success("Xóa tòa nhà thành công");
      loadBuildings();
    } catch (error) {
      if (error.message === "jwt expired") {
        toast.error("Phiên đăng nhập hết hạn, vui lòng đăng nhập lại");
      } else {
        toast.error(error.message || "Có lỗi xảy ra khi xóa tòa nhà");
      }
    }
  };

  // Handlers cho bật/tắt room dialog
  const handleOpenRoomDialog = (mode, room = null) => {
    setRoomDialogMode(mode);
    if (mode === "edit" && room) {
      setRoomFormData({
        _id: room._id,
        name: room.name,
        code: room.code,
        building_id: room.building_id || "",
        floor: room.floor || 1,
        capacity: room.capacity || 30,
        room_type: room.room_type || "classroom",
        facilities: room.facilities || [],
        status: room.status || "active",
      });
    } else {
      setRoomFormData({
        name: "",
        code: "",
        building_id: selectedBuilding?._id || "",
        floor: 1,
        capacity: 30,
        room_type: "classroom",
        facilities: [],
        status: "active",
      });
    }
    setRoomDialogOpen(true);
  };

  const handleCloseRoomDialog = () => {
    setRoomDialogOpen(false);
  };

  // Handler cho room form change
  const handleRoomFormChange = (e) => {
    const { name, value } = e.target;
    setRoomFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Handler cho submit room form
  const handleRoomFormSubmit = async (e) => {
    e.preventDefault();

    // Validate form data
    if (!roomFormData.name.trim() || !roomFormData.code.trim()) {
      toast.error("Vui lòng nhập đầy đủ tên và mã phòng học");
      return;
    }

    // Kiểm tra building_id
    if (!roomFormData.building_id) {
      toast.error("Vui lòng chọn tòa nhà cho phòng học");
      return;
    }

    const roomData = {
      ...roomFormData,
      name: roomFormData.name.trim(),
      code: roomFormData.code.trim(),
      floor: Number(roomFormData.floor),
      capacity: Number(roomFormData.capacity),
    };

    try {
      if (roomDialogMode === "add") {
        await dispatch(createRoom(roomData)).unwrap();
        toast.success("Thêm phòng học thành công");
      } else {
        await dispatch(
          updateRoom({
            id: roomFormData._id,
            data: roomData,
          })
        ).unwrap();
        toast.success("Cập nhật phòng học thành công");
      }

      handleCloseRoomDialog();
      loadRooms();
    } catch (error) {
      if (error.message === "jwt expired") {
        toast.error("Phiên đăng nhập hết hạn, vui lòng đăng nhập lại");
      } else {
        toast.error(
          error.message || "Có lỗi xảy ra khi xử lý thông tin phòng học"
        );
      }
    }
  };

  // Handler cho xóa room
  const handleDeleteRoom = async (roomId) => {
    try {
      await dispatch(deleteRoom(roomId)).unwrap();
      toast.success("Xóa phòng học thành công");
      loadRooms();
    } catch (error) {
      if (error.message === "jwt expired") {
        toast.error("Phiên đăng nhập hết hạn, vui lòng đăng nhập lại");
      } else {
        toast.error(error.message || "Có lỗi xảy ra khi xóa phòng học");
      }
    }
  };

  // Handlers cho xóa
  const openDeleteDialog = (type, item) => {
    setDeleteDialogType(type);
    setItemToDelete(item);
    setDeleteDialogOpen(true);
  };

  const handleDeleteItem = async () => {
    setIsSubmitting(true);
    try {
      if (deleteDialogType === "campus" && itemToDelete) {
        await dispatch(deleteCampus(itemToDelete._id)).unwrap();
        enqueueSnackbar("Xóa cơ sở thành công", { variant: "success" });
        loadCampuses();
      } else if (deleteDialogType === "building" && itemToDelete) {
        await dispatch(deleteBuilding(itemToDelete._id)).unwrap();
        enqueueSnackbar("Xóa tòa nhà thành công", { variant: "success" });
        loadBuildings();
      } else if (deleteDialogType === "room" && itemToDelete) {
        await dispatch(deleteRoom(itemToDelete._id)).unwrap();
        enqueueSnackbar("Xóa phòng học thành công", { variant: "success" });
        loadRooms();
      }
    } catch (error) {
      enqueueSnackbar(
        error.message || `Không thể xóa ${deleteDialogType}. Vui lòng thử lại.`,
        { variant: "error" }
      );
    } finally {
      setIsSubmitting(false);
      setDeleteDialogOpen(false);
    }
  };

  // Handler cho việc chọn cơ sở
  const handleSelectCampus = (campus) => {
    dispatch(setSelectedCampus(campus));
    setTabValue(1); // Chuyển sang tab tòa nhà
  };

  // Handler cho việc chọn tòa nhà
  const handleSelectBuilding = (building) => {
    dispatch(setSelectedBuilding(building));
    setTabValue(1); // Chuyển sang tab phòng học
  };

  const handleBackToCampuses = () => {
    dispatch(setSelectedCampus(null));
    setBuildingSearchTerm("");
    setBuildingPage(0);
  };

  // Thêm handler quay lại tòa nhà từ phòng học
  const handleBackToBuildings = () => {
    dispatch(setSelectedBuilding(null));
    setRoomSearchTerm("");
    setRoomPage(0);
  };

  // Render tùy theo tab đang chọn
  const renderContent = () => {
    if (tabValue === 0) {
      return renderCampusesTab();
    } else if (tabValue === 1) {
      return renderBuildingsTab();
    } else {
      return renderRoomsTab();
    }
  };

  // Render tab cơ sở
  const renderCampusesTab = () => (
    <Box>
      <Box sx={{ mb: 2, display: "flex", justifyContent: "space-between" }}>
        <Box display="flex" alignItems="center">
          <TextField
            size="small"
            variant="outlined"
            placeholder="Tìm kiếm cơ sở..."
            value={campusSearchTerm}
            onChange={handleCampusSearchChange}
            onKeyPress={(e) => {
              if (e.key === "Enter") {
                handleCampusSearch();
              }
            }}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <Search />
                </InputAdornment>
              ),
            }}
            sx={{ mr: 1 }}
          />
          <Button
            variant="contained"
            color="primary"
            onClick={handleCampusSearch}
            startIcon={<Search />}
            size="small"
            sx={{ mr: 1 }}
          >
            Tìm
          </Button>
          <Button
            variant="outlined"
            color="secondary"
            onClick={() => {
              setCampusSearchTerm("");
              loadCampuses();
            }}
            startIcon={<Refresh />}
            size="small"
          >
            Đặt lại
          </Button>
        </Box>
        <Button
          variant="contained"
          color="primary"
          startIcon={<Add />}
          onClick={() => openCampusDialog("create")}
        >
          Thêm cơ sở
        </Button>
      </Box>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell width="5%">STT</TableCell>
              <TableCell width="15%">Mã</TableCell>
              <TableCell width="20%">Tên cơ sở</TableCell>
              <TableCell width="25%">Địa chỉ</TableCell>
              <TableCell width="10%">Trạng thái</TableCell>
              <TableCell width="15%">Thao tác</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {isLoading ? (
              <TableRow>
                <TableCell colSpan={6} align="center">
                  <CircularProgress size={40} thickness={4} />
                </TableCell>
              </TableRow>
            ) : campuses.length === 0 ? (
              <TableRow>
                <TableCell colSpan={6} align="center">
                  <Typography variant="body1">
                    Không có cơ sở nào được tìm thấy
                  </Typography>
                </TableCell>
              </TableRow>
            ) : (
              campuses.map((campus, index) => (
                <TableRow
                  key={campus._id}
                  sx={{
                    cursor: "pointer",
                    "&:hover": { backgroundColor: "rgba(0, 0, 0, 0.04)" },
                  }}
                  onClick={() => handleSelectCampus(campus)}
                >
                  <TableCell>
                    {campusPage * campusRowsPerPage + index + 1}
                  </TableCell>
                  <TableCell>{campus.code}</TableCell>
                  <TableCell>{campus.name}</TableCell>
                  <TableCell>{campus.address}</TableCell>
                  <TableCell>
                    <Chip
                      label={
                        campus.status === "active"
                          ? "Hoạt động"
                          : campus.status === "maintenance"
                          ? "Bảo trì"
                          : "Không hoạt động"
                      }
                      color={
                        campus.status === "active"
                          ? "success"
                          : campus.status === "maintenance"
                          ? "warning"
                          : "error"
                      }
                      size="small"
                    />
                  </TableCell>
                  <TableCell>
                    <Tooltip title="Chỉnh sửa">
                      <IconButton
                        color="primary"
                        onClick={(e) => {
                          e.stopPropagation();
                          openCampusDialog("edit", campus);
                        }}
                      >
                        <Edit />
                      </IconButton>
                    </Tooltip>
                    <Tooltip title="Xóa">
                      <IconButton
                        color="error"
                        onClick={(e) => {
                          e.stopPropagation();
                          openDeleteDialog("campus", campus);
                        }}
                      >
                        <Delete />
                      </IconButton>
                    </Tooltip>
                  </TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </TableContainer>

      <TablePagination
        component="div"
        count={campusPagination.totalCount}
        page={campusPage}
        onPageChange={handleCampusChangePage}
        rowsPerPage={campusRowsPerPage}
        onRowsPerPageChange={handleCampusChangeRowsPerPage}
        rowsPerPageOptions={[5, 10, 25, 50]}
        labelRowsPerPage="Số hàng mỗi trang:"
        labelDisplayedRows={({ from, to, count }) =>
          `${from}-${to} của ${count !== -1 ? count : `hơn ${to}`}`
        }
      />
    </Box>
  );

  // Render tab tòa nhà
  const renderBuildingsTab = () => (
    <Box>
      {selectedCampus && (
        <Box sx={{ mb: 3 }}>
          <Button
            variant="outlined"
            startIcon={<ArrowBack />}
            onClick={handleBackToCampuses}
            sx={{ mb: 2 }}
          >
            Quay lại danh sách cơ sở
          </Button>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              {selectedCampus.name} ({selectedCampus.code})
            </Typography>
            <Typography variant="body2" color="textSecondary">
              Địa chỉ: {selectedCampus.address}
            </Typography>
            {selectedCampus.description && (
              <Typography variant="body2" color="textSecondary">
                Mô tả: {selectedCampus.description}
              </Typography>
            )}
          </Paper>
        </Box>
      )}

      <Paper sx={{ p: 2, mb: 3 }}>
        <Box
          display="flex"
          flexDirection={{ xs: "column", sm: "row" }}
          alignItems={{ sm: "center" }}
          justifyContent="space-between"
          gap={2}
        >
          <Box display="flex" alignItems="center" gap={1} flexGrow={1}>
            <TextField
              label="Tìm kiếm tòa nhà"
              variant="outlined"
              size="small"
              fullWidth
              value={buildingSearchTerm}
              onChange={handleBuildingSearchChange}
              onKeyPress={(e) => e.key === "Enter" && handleBuildingSearch()}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <Search />
                  </InputAdornment>
                ),
              }}
            />
            <Button
              variant="contained"
              color="primary"
              onClick={handleBuildingSearch}
              startIcon={<Search />}
            >
              Tìm
            </Button>
          </Box>

          <Box display="flex" gap={2}>
            <Button
              variant="outlined"
              startIcon={<Refresh />}
              onClick={loadBuildings}
            >
              Làm mới
            </Button>
            <Button
              variant="contained"
              startIcon={<Add />}
              onClick={() => openBuildingDialog("create")}
            >
              Thêm tòa nhà
            </Button>
          </Box>
        </Box>
      </Paper>

      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }}>
          <TableHead>
            <TableRow>
              <TableCell>Tên tòa nhà</TableCell>
              <TableCell>Mã</TableCell>
              <TableCell>Cơ sở</TableCell>
              <TableCell align="center">Số tầng</TableCell>
              <TableCell align="right">Hành động</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {isLoading && buildings.length === 0 ? (
              <TableRow>
                <TableCell colSpan={5} align="center">
                  <CircularProgress size={30} />
                </TableCell>
              </TableRow>
            ) : buildings.length === 0 ? (
              <TableRow>
                <TableCell colSpan={5} align="center">
                  Không tìm thấy tòa nhà nào
                </TableCell>
              </TableRow>
            ) : (
              buildings.map((building) => (
                <TableRow key={building._id}>
                  <TableCell>
                    <Box display="flex" alignItems="center">
                      <Domain sx={{ mr: 1, color: "primary.main" }} />
                      <Typography variant="body1">{building.name}</Typography>
                    </Box>
                  </TableCell>
                  <TableCell>{building.code}</TableCell>
                  <TableCell>
                    {building.campus_id?.name || "Không có thông tin"}
                  </TableCell>
                  <TableCell align="center">{building.floors_count}</TableCell>
                  <TableCell align="right">
                    <Box display="flex" justifyContent="flex-end">
                      <Tooltip title="Xem phòng học">
                        <IconButton
                          color="primary"
                          onClick={() => handleSelectBuilding(building)}
                        >
                          <Room />
                        </IconButton>
                      </Tooltip>
                      <Tooltip title="Sửa tòa nhà">
                        <IconButton
                          color="primary"
                          onClick={() => {
                            dispatch(setSelectedBuilding(building));
                            openBuildingDialog("edit", building);
                          }}
                        >
                          <Edit />
                        </IconButton>
                      </Tooltip>
                      <Tooltip title="Xóa tòa nhà">
                        <IconButton
                          color="error"
                          onClick={() => openDeleteDialog("building", building)}
                        >
                          <Delete />
                        </IconButton>
                      </Tooltip>
                    </Box>
                  </TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
        <TablePagination
          rowsPerPageOptions={[5, 10, 25]}
          component="div"
          count={buildingPagination.totalCount}
          rowsPerPage={buildingRowsPerPage}
          page={buildingPage}
          onPageChange={handleBuildingChangePage}
          onRowsPerPageChange={handleBuildingChangeRowsPerPage}
          labelRowsPerPage="Dòng mỗi trang:"
          labelDisplayedRows={({ from, to, count }) =>
            `${from}-${to} trên ${count}`
          }
        />
      </TableContainer>
    </Box>
  );

  // Render tab phòng học
  const renderRoomsTab = () => (
    <Box>
      {selectedBuilding ? (
        <>
          <Box sx={{ mb: 3 }}>
            <Button
              variant="outlined"
              startIcon={<ArrowBack />}
              onClick={handleBackToBuildings}
              sx={{ mb: 2 }}
            >
              Quay lại danh sách tòa nhà
            </Button>
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6" gutterBottom>
                {selectedBuilding.name} ({selectedBuilding.code})
              </Typography>
              <Typography variant="body2" color="textSecondary">
                Cơ sở:{" "}
                {selectedBuilding.campus_id?.name || "Không có thông tin"}
              </Typography>
              <Typography variant="body2" color="textSecondary">
                Số tầng: {selectedBuilding.floors_count}
              </Typography>
              {selectedBuilding.description && (
                <Typography variant="body2" color="textSecondary">
                  Mô tả: {selectedBuilding.description}
                </Typography>
              )}
            </Paper>
          </Box>

          <Paper sx={{ p: 2, mb: 3 }}>
            <Box
              display="flex"
              flexDirection={{ xs: "column", sm: "row" }}
              alignItems={{ sm: "center" }}
              justifyContent="space-between"
              gap={2}
            >
              <Box display="flex" alignItems="center" gap={1} flexGrow={1}>
                <TextField
                  label="Tìm kiếm phòng học"
                  variant="outlined"
                  size="small"
                  fullWidth
                  value={roomSearchTerm}
                  onChange={handleRoomSearchChange}
                  onKeyPress={(e) => e.key === "Enter" && handleRoomSearch()}
                  InputProps={{
                    startAdornment: (
                      <InputAdornment position="start">
                        <Search />
                      </InputAdornment>
                    ),
                  }}
                />
                <Button
                  variant="contained"
                  color="primary"
                  onClick={handleRoomSearch}
                  startIcon={<Search />}
                >
                  Tìm
                </Button>
              </Box>

              <Box display="flex" gap={2}>
                <Button
                  variant="outlined"
                  startIcon={<Refresh />}
                  onClick={loadRooms}
                >
                  Làm mới
                </Button>
                <Button
                  variant="contained"
                  startIcon={<Add />}
                  onClick={() => openRoomDialog("create")}
                >
                  Thêm phòng học
                </Button>
              </Box>
            </Box>
          </Paper>

          <TableContainer component={Paper}>
            <Table sx={{ minWidth: 650 }}>
              <TableHead>
                <TableRow>
                  <TableCell>Tên phòng</TableCell>
                  <TableCell>Mã phòng</TableCell>
                  <TableCell align="center">Tầng</TableCell>
                  <TableCell align="center">Sức chứa</TableCell>
                  <TableCell>Loại phòng</TableCell>
                  <TableCell align="right">Hành động</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {isLoading && rooms.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={6} align="center">
                      <CircularProgress size={30} />
                    </TableCell>
                  </TableRow>
                ) : rooms.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={6} align="center">
                      Không tìm thấy phòng học nào
                    </TableCell>
                  </TableRow>
                ) : (
                  rooms.map((room) => (
                    <TableRow key={room._id}>
                      <TableCell>
                        <Box display="flex" alignItems="center">
                          <Room sx={{ mr: 1, color: "primary.main" }} />
                          <Typography variant="body1">{room.name}</Typography>
                        </Box>
                      </TableCell>
                      <TableCell>{room.code}</TableCell>
                      <TableCell align="center">{room.floor}</TableCell>
                      <TableCell align="center">{room.capacity}</TableCell>
                      <TableCell>
                        <Chip
                          label={
                            room.type === "classroom"
                              ? "Phòng học"
                              : room.type === "lab"
                              ? "Phòng thực hành"
                              : room.type
                          }
                          color={
                            room.type === "classroom"
                              ? "primary"
                              : room.type === "lab"
                              ? "secondary"
                              : "default"
                          }
                          size="small"
                        />
                      </TableCell>
                      <TableCell align="right">
                        <Box display="flex" justifyContent="flex-end">
                          <Tooltip title="Sửa phòng học">
                            <IconButton
                              color="primary"
                              onClick={() => {
                                setItemToDelete(room);
                                openRoomDialog("edit", room);
                              }}
                            >
                              <Edit />
                            </IconButton>
                          </Tooltip>
                          <Tooltip title="Xóa phòng học">
                            <IconButton
                              color="error"
                              onClick={() => openDeleteDialog("room", room)}
                            >
                              <Delete />
                            </IconButton>
                          </Tooltip>
                        </Box>
                      </TableCell>
                    </TableRow>
                  ))
                )}
              </TableBody>
            </Table>
            <TablePagination
              rowsPerPageOptions={[5, 10, 25]}
              component="div"
              count={roomPagination.totalCount}
              rowsPerPage={roomRowsPerPage}
              page={roomPage}
              onPageChange={handleRoomChangePage}
              onRowsPerPageChange={handleRoomChangeRowsPerPage}
              labelRowsPerPage="Dòng mỗi trang:"
              labelDisplayedRows={({ from, to, count }) =>
                `${from}-${to} trên ${count}`
              }
            />
          </TableContainer>
        </>
      ) : (
        <Box
          display="flex"
          justifyContent="center"
          alignItems="center"
          minHeight="200px"
        >
          <Typography variant="h6" color="textSecondary">
            Vui lòng chọn một tòa nhà để xem danh sách phòng học
          </Typography>
        </Box>
      )}
    </Box>
  );

  return (
    <Box>
      <Typography variant="h5" gutterBottom>
        Quản lý cơ sở vật chất
      </Typography>

      <Paper sx={{ mb: 3 }}>
        <Tabs
          value={tabValue}
          onChange={handleTabChange}
          indicatorColor="primary"
          textColor="primary"
          variant="fullWidth"
        >
          <Tab label="Cơ sở" />
          <Tab label="Tòa nhà" />
          <Tab label="Phòng học" />
        </Tabs>
      </Paper>

      {renderContent()}

      {/* Dialog tạo/sửa cơ sở */}
      <Dialog
        open={campusDialogOpen}
        onClose={() => setCampusDialogOpen(false)}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>
          {campusDialogMode === "create" ? "Thêm cơ sở mới" : "Cập nhật cơ sở"}
        </DialogTitle>
        <DialogContent>
          <Grid container spacing={2} sx={{ mt: 1 }}>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                label="Tên cơ sở"
                name="name"
                value={campusFormData.name}
                onChange={handleCampusFormChange}
                required
                margin="dense"
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                label="Mã cơ sở"
                name="code"
                value={campusFormData.code}
                onChange={handleCampusFormChange}
                required
                margin="dense"
                disabled={campusDialogMode === "edit"}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Địa chỉ"
                name="address"
                value={campusFormData.address}
                onChange={handleCampusFormChange}
                margin="dense"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Mô tả"
                name="description"
                value={campusFormData.description}
                onChange={handleCampusFormChange}
                margin="dense"
                multiline
                rows={3}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <FormControl fullWidth margin="dense">
                <InputLabel>Trạng thái</InputLabel>
                <Select
                  name="status"
                  value={campusFormData.status}
                  onChange={handleCampusFormChange}
                  label="Trạng thái"
                >
                  <MenuItem value="active">Hoạt động</MenuItem>
                  <MenuItem value="maintenance">Bảo trì</MenuItem>
                  <MenuItem value="inactive">Không hoạt động</MenuItem>
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                label="URL Hình ảnh"
                name="image_url"
                value={campusFormData.image_url}
                onChange={handleCampusFormChange}
                margin="dense"
              />
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setCampusDialogOpen(false)}>Hủy</Button>
          <Button
            onClick={handleCampusFormSubmit}
            color="primary"
            variant="contained"
            disabled={isSubmitting}
          >
            {isSubmitting ? (
              <CircularProgress size={24} />
            ) : campusDialogMode === "create" ? (
              "Thêm"
            ) : (
              "Cập nhật"
            )}
          </Button>
        </DialogActions>
      </Dialog>

      {/* Dialog tạo/sửa tòa nhà */}
      <Dialog
        open={buildingDialogOpen}
        onClose={() => setBuildingDialogOpen(false)}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>
          {buildingDialogMode === "create" ? "Thêm tòa nhà mới" : "Sửa tòa nhà"}
        </DialogTitle>
        <DialogContent>
          <Grid container spacing={2} sx={{ mt: 1 }}>
            <Grid item xs={12}>
              <TextField
                name="name"
                label="Tên tòa nhà"
                fullWidth
                required
                value={buildingFormData.name}
                onChange={handleBuildingFormChange}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                name="code"
                label="Mã tòa nhà"
                fullWidth
                required
                value={buildingFormData.code}
                onChange={handleBuildingFormChange}
              />
            </Grid>
            <Grid item xs={12}>
              <FormControl fullWidth required>
                <InputLabel>Chọn cơ sở</InputLabel>
                <Select
                  name="campus_id"
                  value={buildingFormData.campus_id}
                  onChange={handleBuildingFormChange}
                  label="Chọn cơ sở"
                >
                  {localCampusList.map((campus) => (
                    <MenuItem key={campus._id} value={campus._id}>
                      {campus.name} ({campus.code})
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                name="floors_count"
                label="Số tầng"
                type="number"
                fullWidth
                required
                value={buildingFormData.floors_count}
                onChange={handleBuildingFormChange}
                InputProps={{ inputProps: { min: 1 } }}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                name="year_built"
                label="Năm xây dựng"
                type="number"
                fullWidth
                required
                value={buildingFormData.year_built}
                onChange={handleBuildingFormChange}
                InputProps={{
                  inputProps: { min: 1900, max: new Date().getFullYear() },
                }}
              />
            </Grid>
            <Grid item xs={12}>
              <FormControl fullWidth>
                <InputLabel>Trạng thái</InputLabel>
                <Select
                  name="status"
                  value={buildingFormData.status}
                  onChange={handleBuildingFormChange}
                  label="Trạng thái"
                >
                  <MenuItem value="active">Hoạt động</MenuItem>
                  <MenuItem value="maintenance">Bảo trì</MenuItem>
                  <MenuItem value="inactive">Ngưng hoạt động</MenuItem>
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12}>
              <TextField
                name="facilities"
                label="Các cơ sở"
                fullWidth
                multiline
                rows={2}
                value={buildingFormData.facilities.join(", ")}
                onChange={handleBuildingFormChange}
                placeholder="Danh sách cơ sở, phân cách bằng dấu phẩy"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                name="image_url"
                label="URL hình ảnh"
                fullWidth
                value={buildingFormData.image_url}
                onChange={handleBuildingFormChange}
              />
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setBuildingDialogOpen(false)}>Hủy</Button>
          <Button
            onClick={handleBuildingFormSubmit}
            variant="contained"
            color="primary"
            disabled={!buildingFormData.name || !buildingFormData.code}
          >
            {buildingDialogMode === "create" ? "Tạo mới" : "Cập nhật"}
          </Button>
        </DialogActions>
      </Dialog>

      {/* Dialog tạo/sửa phòng học */}
      <Dialog
        open={roomDialogOpen}
        onClose={() => setRoomDialogOpen(false)}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>
          {roomDialogMode === "create" ? "Thêm phòng học mới" : "Sửa phòng học"}
        </DialogTitle>
        <DialogContent>
          <Grid container spacing={2} sx={{ mt: 1 }}>
            <Grid item xs={12}>
              <TextField
                name="name"
                label="Tên phòng"
                fullWidth
                required
                value={roomFormData.name}
                onChange={handleRoomFormChange}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                name="code"
                label="Mã phòng"
                fullWidth
                required
                value={roomFormData.code}
                onChange={handleRoomFormChange}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                name="floor"
                label="Tầng"
                type="number"
                fullWidth
                required
                value={roomFormData.floor}
                onChange={handleRoomFormChange}
                InputProps={{
                  inputProps: {
                    min: 1,
                    max: selectedCampus ? selectedCampus.floors : 100,
                  },
                }}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                name="capacity"
                label="Sức chứa"
                type="number"
                fullWidth
                required
                value={roomFormData.capacity}
                onChange={handleRoomFormChange}
                InputProps={{ inputProps: { min: 1 } }}
              />
            </Grid>
            <Grid item xs={12}>
              <FormControl fullWidth required>
                <InputLabel>Loại phòng</InputLabel>
                <Select
                  name="type"
                  value={roomFormData.type}
                  onChange={handleRoomFormChange}
                  label="Loại phòng"
                >
                  <MenuItem value="classroom">Phòng học</MenuItem>
                  <MenuItem value="lab">Phòng thực hành</MenuItem>
                  <MenuItem value="office">Phòng làm việc</MenuItem>
                  <MenuItem value="meeting">Phòng họp</MenuItem>
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12}>
              <TextField
                name="equipment"
                label="Thiết bị"
                fullWidth
                multiline
                rows={2}
                value={roomFormData.equipment}
                onChange={handleRoomFormChange}
                placeholder="Danh sách thiết bị, phân cách bằng dấu phẩy"
              />
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setRoomDialogOpen(false)}>Hủy</Button>
          <Button
            onClick={handleRoomFormSubmit}
            variant="contained"
            color="primary"
            disabled={!roomFormData.name || !roomFormData.code}
          >
            {roomDialogMode === "create" ? "Tạo mới" : "Cập nhật"}
          </Button>
        </DialogActions>
      </Dialog>

      {/* Dialog xác nhận xóa */}
      <Dialog
        open={deleteDialogOpen}
        onClose={() => setDeleteDialogOpen(false)}
      >
        <DialogTitle>
          Xác nhận xóa{" "}
          {deleteDialogType === "campus"
            ? "cơ sở"
            : deleteDialogType === "building"
            ? "tòa nhà"
            : "phòng học"}
        </DialogTitle>
        <DialogContent>
          <DialogContentText>
            Bạn có chắc chắn muốn xóa{" "}
            {deleteDialogType === "campus"
              ? "cơ sở"
              : deleteDialogType === "building"
              ? "tòa nhà"
              : "phòng học"}{" "}
            "{itemToDelete?.name}"? Hành động này không thể hoàn tác.
            {deleteDialogType === "campus" &&
              " Tất cả tòa nhà và phòng học trong cơ sở này cũng sẽ bị xóa."}
            {deleteDialogType === "building" &&
              " Tất cả phòng học trong tòa nhà này cũng sẽ bị xóa."}
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDeleteDialogOpen(false)}>Hủy</Button>
          <Button onClick={handleDeleteItem} variant="contained" color="error">
            Xóa
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default FacilitiesPage;
