import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

// Thunks
export const fetchAllUsers = createAsyncThunk(
  "admin/fetchAllUsers",
  async (
    { page = 1, limit = 10, search = "", role = "" },
    { getState, rejectWithValue }
  ) => {
    try {
      const { token } = getState().auth;
      const response = await axios.get(
        `${API_URL}/users?page=${page}&limit=${limit}&search=${search}${
          role ? `&role=${role}` : ""
        }`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      return response.data;
    } catch (error) {
      return rejectWithValue(
        error.response?.data?.message || "Không thể lấy danh sách người dùng"
      );
    }
  }
);

export const approveUser = createAsyncThunk(
  "admin/approveUser",
  async (userId, { getState, rejectWithValue }) => {
    try {
      const { token } = getState().auth;
      const response = await axios.put(
        `${API_URL}/users/${userId}/approve`,
        {},
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      return { userId, data: response.data };
    } catch (error) {
      return rejectWithValue(
        error.response?.data?.message || "Không thể phê duyệt người dùng"
      );
    }
  }
);

export const updateUserRole = createAsyncThunk(
  "admin/updateUserRole",
  async ({ userId, role }, { getState, rejectWithValue }) => {
    try {
      const { token } = getState().auth;
      const response = await axios.put(
        `${API_URL}/users/${userId}/role`,
        { role },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      return { userId, role, data: response.data };
    } catch (error) {
      return rejectWithValue(
        error.response?.data?.message || "Không thể cập nhật vai trò người dùng"
      );
    }
  }
);

export const deleteUser = createAsyncThunk(
  "admin/deleteUser",
  async (userId, { getState, rejectWithValue }) => {
    try {
      const { token } = getState().auth;
      await axios.delete(`${API_URL}/users/${userId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      return userId;
    } catch (error) {
      return rejectWithValue(
        error.response?.data?.message || "Không thể xóa người dùng"
      );
    }
  }
);

// Initial state
const initialState = {
  users: [],
  userStats: {
    totalUsers: 0,
    students: 0,
    teachers: 0,
    pendingTeachers: 0,
  },
  isLoading: false,
  error: null,
  pagination: {
    page: 1,
    limit: 10,
    totalPages: 1,
    totalCount: 0,
  },
};

// Slice
const adminSlice = createSlice({
  name: "admin",
  initialState,
  reducers: {
    clearError: (state) => {
      state.error = null;
    },
  },
  extraReducers: (builder) => {
    builder
      // Fetch All Users
      .addCase(fetchAllUsers.pending, (state) => {
        state.isLoading = true;
        state.error = null;
      })
      .addCase(fetchAllUsers.fulfilled, (state, action) => {
        state.isLoading = false;
        state.users = action.payload.data;
        state.userStats = {
          totalUsers: action.payload.totalCount || 0,
          students: action.payload.stats?.students || 0,
          teachers: action.payload.stats?.teachers || 0,
          pendingTeachers: action.payload.stats?.pendingTeachers || 0,
        };
        state.pagination = {
          page: action.payload.page || 1,
          limit: action.payload.limit || 10,
          totalPages: action.payload.totalPages || 1,
          totalCount: action.payload.totalCount || 0,
        };
      })
      .addCase(fetchAllUsers.rejected, (state, action) => {
        state.isLoading = false;
        state.error = action.payload;
      })

      // Approve User
      .addCase(approveUser.pending, (state) => {
        state.isLoading = true;
        state.error = null;
      })
      .addCase(approveUser.fulfilled, (state, action) => {
        state.isLoading = false;
        state.users = state.users.map((user) =>
          user._id === action.payload.userId
            ? { ...user, is_approved: true }
            : user
        );
        // Cập nhật thống kê
        state.userStats.pendingTeachers -= 1;
      })
      .addCase(approveUser.rejected, (state, action) => {
        state.isLoading = false;
        state.error = action.payload;
      })

      // Update User Role
      .addCase(updateUserRole.pending, (state) => {
        state.isLoading = true;
        state.error = null;
      })
      .addCase(updateUserRole.fulfilled, (state, action) => {
        state.isLoading = false;
        state.users = state.users.map((user) =>
          user._id === action.payload.userId
            ? { ...user, role: action.payload.role }
            : user
        );
        // Cập nhật thống kê (có thể làm phức tạp hơn nếu cần)
      })
      .addCase(updateUserRole.rejected, (state, action) => {
        state.isLoading = false;
        state.error = action.payload;
      })

      // Delete User
      .addCase(deleteUser.pending, (state) => {
        state.isLoading = true;
        state.error = null;
      })
      .addCase(deleteUser.fulfilled, (state, action) => {
        state.isLoading = false;
        state.users = state.users.filter((user) => user._id !== action.payload);
        // Cập nhật thống kê
        state.userStats.totalUsers -= 1;
      })
      .addCase(deleteUser.rejected, (state, action) => {
        state.isLoading = false;
        state.error = action.payload;
      });
  },
});

export const { clearError } = adminSlice.actions;

export default adminSlice.reducer;
