import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { useNavigate, useLocation } from "react-router-dom";
import { useSnackbar } from "notistack";
import axios from "../utils/axios";
import {
  Container,
  Box,
  Typography,
  Button,
  Paper,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  CircularProgress,
  Avatar,
  Grid,
  FormHelperText,
  Autocomplete,
} from "@mui/material";
import { setCredentials } from "../redux/slices/authSlice";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

const CompleteRegistrationPage = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const location = useLocation();
  const { enqueueSnackbar } = useSnackbar();
  const [isLoading, setIsLoading] = useState(false);
  const [advisors, setAdvisors] = useState([]);
  const [loadingAdvisors, setLoadingAdvisors] = useState(false);

  // Lấy thông tin từ URL params
  const queryParams = new URLSearchParams(location.search);
  const email = queryParams.get("email");
  const googleId = queryParams.get("googleId");
  const name = queryParams.get("name");
  const avatar = queryParams.get("avatar");

  const [formData, setFormData] = useState({
    role: "",
    fullName: name || "",
    phone: "",
    department: "",
    studentCode: "",
    teacherCode: "",
    major: "",
    class: "",
    year: new Date().getFullYear(),
    advisorId: "",
  });

  const [formErrors, setFormErrors] = useState({
    role: "",
    fullName: "",
    advisorId: "",
  });

  useEffect(() => {
    // Kiểm tra xem có đủ thông tin không
    if (!email || !googleId) {
      enqueueSnackbar("Thiếu thông tin cần thiết để hoàn tất đăng ký", {
        variant: "error",
      });
      navigate("/login");
    }
  }, [email, googleId, navigate, enqueueSnackbar]);

  // Lấy danh sách giáo viên cố vấn khi người dùng chọn vai trò sinh viên
  useEffect(() => {
    if (formData.role === "student") {
      fetchAdvisors();
    }
  }, [formData.role]);

  const fetchAdvisors = async () => {
    setLoadingAdvisors(true);
    try {
      const response = await axios.get(`${API_URL}/users/teachers`);
      if (response.data.success) {
        setAdvisors(response.data.data);
      }
    } catch (error) {
      console.error("Error fetching advisors:", error);
      setAdvisors([]);
    } finally {
      setLoadingAdvisors(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });

    // Xóa lỗi khi người dùng nhập
    if (formErrors[name]) {
      setFormErrors({ ...formErrors, [name]: "" });
    }
  };

  const handleAdvisorChange = (event, newValue) => {
    setFormData({ ...formData, advisorId: newValue?._id || "" });
    if (formErrors.advisorId) {
      setFormErrors({ ...formErrors, advisorId: "" });
    }
  };

  const validateForm = () => {
    let valid = true;
    const errors = { ...formErrors };

    // Kiểm tra vai trò
    if (!formData.role) {
      errors.role = "Vui lòng chọn vai trò";
      valid = false;
    }

    // Kiểm tra họ tên
    if (!formData.fullName) {
      errors.fullName = "Họ tên là bắt buộc";
      valid = false;
    }

    // Kiểm tra giáo viên cố vấn cho sinh viên
    if (formData.role === "student" && !formData.advisorId) {
      errors.advisorId = "Vui lòng chọn giáo viên cố vấn";
      valid = false;
    }

    setFormErrors(errors);
    return valid;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsLoading(true);
    try {
      // Chuẩn bị dữ liệu
      const registrationData = {
        email,
        googleId,
        fullName: formData.fullName,
        role: formData.role,
        avatarUrl: avatar,
      };

      // Thêm thông tin cố vấn cho sinh viên
      if (formData.role === "student" && formData.advisorId) {
        registrationData.advisor_id = formData.advisorId;
      }

      // Thêm thông tin trường học
      registrationData.school_info = {
        department: formData.department,
        major: formData.major,
        class: formData.class,
        year: formData.year,
      };

      // Thêm mã sinh viên/giáo viên
      if (formData.role === "student" && formData.studentCode) {
        registrationData.school_info.student_code = formData.studentCode;
      } else if (formData.role === "teacher" && formData.teacherCode) {
        registrationData.school_info.teacher_code = formData.teacherCode;
      }

      // Thêm số điện thoại
      if (formData.phone) {
        registrationData.contact = {
          phone: formData.phone,
        };
      }

      const response = await axios.post(
        `${API_URL}/auth/google-complete`,
        registrationData
      );

      if (response.data.success) {
        dispatch(
          setCredentials({
            token: response.data.token,
            user: response.data.user,
          })
        );

        enqueueSnackbar(response.data.message || "Đăng ký thành công!", {
          variant: "success",
        });

        // Chuyển hướng đến trang chờ phê duyệt
        navigate("/pending-approval");
      }
    } catch (error) {
      const errorMessage = error.response?.data?.message || "Đăng ký thất bại";
      enqueueSnackbar(errorMessage, { variant: "error" });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Container component="main" maxWidth="md">
      <Box
        sx={{
          marginTop: 4,
          marginBottom: 4,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <Paper
          elevation={3}
          sx={{
            padding: 4,
            width: "100%",
            borderRadius: 3,
          }}
        >
          <Box
            sx={{
              display: "flex",
              alignItems: "center",
              mb: 4,
              flexDirection: "column",
            }}
          >
            <Typography component="h1" variant="h4" gutterBottom>
              Hoàn tất đăng ký
            </Typography>

            <Typography variant="body1" color="text.secondary" mb={2}>
              Vui lòng cung cấp thêm thông tin để hoàn tất đăng ký
            </Typography>

            {avatar && (
              <Avatar
                src={avatar}
                alt={name || email}
                sx={{ width: 80, height: 80, mb: 2 }}
              />
            )}

            <Typography variant="subtitle1" gutterBottom>
              {email}
            </Typography>
          </Box>

          <Box component="form" onSubmit={handleSubmit} noValidate>
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <FormControl fullWidth required error={!!formErrors.role}>
                  <InputLabel id="role-label">Vai trò</InputLabel>
                  <Select
                    labelId="role-label"
                    id="role"
                    name="role"
                    value={formData.role}
                    label="Vai trò *"
                    onChange={handleChange}
                  >
                    <MenuItem value="student">Sinh viên</MenuItem>
                    <MenuItem value="teacher">Giảng viên</MenuItem>
                  </Select>
                  {formErrors.role && (
                    <FormHelperText>{formErrors.role}</FormHelperText>
                  )}
                </FormControl>
              </Grid>

              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="fullName"
                  label="Họ và tên"
                  name="fullName"
                  value={formData.fullName}
                  onChange={handleChange}
                  error={!!formErrors.fullName}
                  helperText={formErrors.fullName}
                />
              </Grid>

              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  id="phone"
                  label="Số điện thoại"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                />
              </Grid>

              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  id="department"
                  label="Khoa"
                  name="department"
                  value={formData.department}
                  onChange={handleChange}
                />
              </Grid>

              {formData.role === "student" && (
                <>
                  <Grid item xs={12} md={6}>
                    <TextField
                      fullWidth
                      id="studentCode"
                      label="Mã sinh viên"
                      name="studentCode"
                      value={formData.studentCode}
                      onChange={handleChange}
                    />
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <TextField
                      fullWidth
                      id="major"
                      label="Ngành học"
                      name="major"
                      value={formData.major}
                      onChange={handleChange}
                    />
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <TextField
                      fullWidth
                      id="class"
                      label="Lớp"
                      name="class"
                      value={formData.class}
                      onChange={handleChange}
                    />
                  </Grid>

                  <Grid item xs={12} md={6}>
                    <TextField
                      fullWidth
                      id="year"
                      label="Khóa học"
                      name="year"
                      type="number"
                      value={formData.year}
                      onChange={handleChange}
                    />
                  </Grid>

                  <Grid item xs={12}>
                    <Autocomplete
                      id="advisorId"
                      options={advisors}
                      getOptionLabel={(option) =>
                        `${option.full_name} (${option.email})`
                      }
                      loading={loadingAdvisors}
                      onChange={handleAdvisorChange}
                      renderInput={(params) => (
                        <TextField
                          {...params}
                          required
                          label="Giáo viên cố vấn"
                          error={!!formErrors.advisorId}
                          helperText={formErrors.advisorId}
                          InputProps={{
                            ...params.InputProps,
                            endAdornment: (
                              <>
                                {loadingAdvisors ? (
                                  <CircularProgress color="inherit" size={20} />
                                ) : null}
                                {params.InputProps.endAdornment}
                              </>
                            ),
                          }}
                        />
                      )}
                    />
                  </Grid>
                </>
              )}

              {formData.role === "teacher" && (
                <Grid item xs={12} md={6}>
                  <TextField
                    fullWidth
                    id="teacherCode"
                    label="Mã giảng viên"
                    name="teacherCode"
                    value={formData.teacherCode}
                    onChange={handleChange}
                  />
                </Grid>
              )}
            </Grid>

            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2, py: 1.5 }}
              disabled={isLoading}
            >
              {isLoading ? (
                <CircularProgress size={24} color="inherit" />
              ) : (
                "Hoàn tất đăng ký"
              )}
            </Button>
          </Box>
        </Paper>
      </Box>
    </Container>
  );
};

export default CompleteRegistrationPage;
