import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useSnackbar } from "notistack";
import axios from "axios";
import {
  Box,
  Typography,
  Paper,
  Button,
  IconButton,
  TextField,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TablePagination,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  Grid,
  Tooltip,
  CircularProgress,
  InputAdornment,
  Tabs,
  Tab,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Chip,
  Autocomplete,
  Collapse,
  Card,
  CardContent,
  FormHelperText,
} from "@mui/material";
import {
  Add,
  Delete,
  Edit,
  Search,
  Refresh,
  School,
  People,
} from "@mui/icons-material";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

const ClassesPage = () => {
  const dispatch = useDispatch();
  const { enqueueSnackbar } = useSnackbar();
  const { token } = useSelector((state) => state.auth);

  // State
  const [tabValue, setTabValue] = useState(0);
  const [classes, setClasses] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [totalCount, setTotalCount] = useState(0);
  const [searchTerm, setSearchTerm] = useState("");

  // Lookup data for dropdowns
  const [teachers, setTeachers] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [students, setStudents] = useState([]);
  const [courses, setCourses] = useState([]);

  // Dialog states
  const [dialogOpen, setDialogOpen] = useState(false);
  const [dialogMode, setDialogMode] = useState("create");
  const [selectedClass, setSelectedClass] = useState(null);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);

  // Main Class Form Data
  const [mainClassForm, setMainClassForm] = useState({
    name: "",
    code: "",
    department_id: "",
    advisor_id: "",
    description: "",
    students: [],
  });

  // Teaching Class Form Data
  const [teachingClassForm, setTeachingClassForm] = useState({
    class_name: "",
    course_id: "",
    semester: "",
    academic_year: "",
    teacher_id: "",
    main_class_id: "",
    selected_students: [],
    description: "",
  });

  // State nâng cao
  const [filterOptions, setFilterOptions] = useState({
    department: "",
    teacher: "",
    course: "",
    semester: "",
  });

  // Thêm states cho thống kê
  const [stats, setStats] = useState({
    mainClasses: 0,
    teachingClasses: 0,
    students: 0,
    teachers: 0,
  });

  // Thêm state cho lỗi validation
  const [formErrors, setFormErrors] = useState({
    main: {
      name: false,
      code: false,
    },
    teaching: {
      class_name: false,
      course_id: false,
      semester: false,
      academic_year: false,
    },
  });

  // Load data on mount and when pagination/search changes
  useEffect(() => {
    loadClasses();
  }, [page, rowsPerPage, tabValue]);

  useEffect(() => {
    loadLookupData();
  }, []);

  useEffect(() => {
    loadStats();
  }, []);

  const loadLookupData = async () => {
    try {
      setIsLoading(true);
      // Load teachers với tham số tìm kiếm nâng cao
      const teachersResponse = await axios.get(
        `${API_URL}/users?role=teacher&status=approved&limit=200&sort=full_name`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setTeachers(teachersResponse.data.data || []);

      // Load departments
      const departmentsResponse = await axios.get(
        `${API_URL}/departments?limit=100`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setDepartments(departmentsResponse.data.data || []);

      // Load students
      const studentsResponse = await axios.get(
        `${API_URL}/users?role=student&status=approved&limit=200&sort=full_name`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setStudents(studentsResponse.data.data || []);

      // Load courses
      const coursesResponse = await axios.get(
        `${API_URL}/courses?limit=100&sort=name`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setCourses(coursesResponse.data.data || []);
      setIsLoading(false);
    } catch (error) {
      console.error("Lỗi khi tải dữ liệu tham chiếu:", error);
      enqueueSnackbar("Lỗi khi tải dữ liệu tham chiếu", { variant: "error" });
      setIsLoading(false);
    }
  };

  const loadClasses = async () => {
    try {
      setIsLoading(true);
      const endpoint = tabValue === 0 ? "classes/main" : "classes/teaching";

      // Xây dựng query params với bộ lọc
      let queryParams = `page=${
        page + 1
      }&limit=${rowsPerPage}&search=${searchTerm}`;

      if (tabValue === 0 && filterOptions.department) {
        queryParams += `&department_id=${filterOptions.department}`;
      }

      if (tabValue === 0 && filterOptions.teacher) {
        queryParams += `&advisor_id=${filterOptions.teacher}`;
      }

      if (tabValue === 1 && filterOptions.course) {
        queryParams += `&course_id=${filterOptions.course}`;
      }

      if (tabValue === 1 && filterOptions.teacher) {
        queryParams += `&teacher_id=${filterOptions.teacher}`;
      }

      if (tabValue === 1 && filterOptions.semester) {
        queryParams += `&semester=${filterOptions.semester}`;
      }

      const response = await axios.get(
        `${API_URL}/${endpoint}?${queryParams}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      setClasses(response.data.data || []);
      setTotalCount(response.data.totalCount || 0);
      setIsLoading(false);
    } catch (error) {
      console.error("Lỗi khi tải danh sách lớp:", error);
      enqueueSnackbar("Lỗi khi tải danh sách lớp", { variant: "error" });
      setIsLoading(false);
    }
  };

  const handleSearch = () => {
    setPage(0);
    loadClasses();
  };

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
    setPage(0);
    setSearchTerm("");
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  // Thêm hàm xử lý lọc
  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilterOptions({
      ...filterOptions,
      [name]: value,
    });
  };

  // Thêm hàm áp dụng bộ lọc
  const applyFilters = () => {
    setPage(0);
    loadClasses();
  };

  // Thêm hàm xóa bộ lọc
  const clearFilters = () => {
    setFilterOptions({
      department: "",
      teacher: "",
      course: "",
      semester: "",
    });
    setSearchTerm("");
    setPage(0);
    loadClasses();
  };

  // Dialog handlers
  const openDialog = (mode, classItem = null) => {
    setDialogMode(mode);

    if (tabValue === 0) {
      // Main Class
      if (mode === "edit" && classItem) {
        setMainClassForm({
          name: classItem.name,
          code: classItem.code,
          department_id: classItem.department_id?._id || "",
          advisor_id: classItem.advisor_id?._id || "",
          description: classItem.description || "",
          students: classItem.students || [],
        });
        setSelectedClass(classItem);
      } else {
        setMainClassForm({
          name: "",
          code: "",
          department_id: "",
          advisor_id: "",
          description: "",
          students: [],
        });
      }
    } else {
      // Teaching Class
      if (mode === "edit" && classItem) {
        setTeachingClassForm({
          class_name: classItem.class_name,
          course_id: classItem.course_id?._id || "",
          semester: classItem.semester || "",
          academic_year: classItem.academic_year || "",
          teacher_id: classItem.teacher_id?._id || "",
          main_class_id: classItem.main_class_id?._id || "",
          selected_students: classItem.students || [],
          description: classItem.description || "",
        });
        setSelectedClass(classItem);
      } else {
        setTeachingClassForm({
          class_name: "",
          course_id: "",
          semester: "",
          academic_year: "",
          teacher_id: "",
          main_class_id: "",
          selected_students: [],
          description: "",
        });
      }
    }

    setDialogOpen(true);
  };

  // Cập nhật hàm handleMainClassFormChange để có validation
  const handleMainClassFormChange = (e) => {
    const { name, value } = e.target;
    setMainClassForm({
      ...mainClassForm,
      [name]: value,
    });

    // Xóa lỗi khi người dùng nhập liệu
    if (name in formErrors.main && value.trim() !== "") {
      setFormErrors({
        ...formErrors,
        main: {
          ...formErrors.main,
          [name]: false,
        },
      });
    }
  };

  // Cập nhật hàm handleTeachingClassFormChange để có validation
  const handleTeachingClassFormChange = (e) => {
    const { name, value } = e.target;
    setTeachingClassForm({
      ...teachingClassForm,
      [name]: value,
    });

    // Xóa lỗi khi người dùng nhập liệu
    if (name in formErrors.teaching && value.trim() !== "") {
      setFormErrors({
        ...formErrors,
        teaching: {
          ...formErrors.teaching,
          [name]: false,
        },
      });
    }
  };

  // Thêm hàm validateMainClassForm
  const validateMainClassForm = () => {
    const errors = {
      name: mainClassForm.name.trim() === "",
      code: mainClassForm.code.trim() === "",
    };

    setFormErrors({
      ...formErrors,
      main: errors,
    });

    return !Object.values(errors).some(Boolean);
  };

  // Thêm hàm validateTeachingClassForm
  const validateTeachingClassForm = () => {
    const errors = {
      class_name: teachingClassForm.class_name.trim() === "",
      course_id: teachingClassForm.course_id === "",
      semester: teachingClassForm.semester.trim() === "",
      academic_year: teachingClassForm.academic_year.trim() === "",
    };

    setFormErrors({
      ...formErrors,
      teaching: errors,
    });

    return !Object.values(errors).some(Boolean);
  };

  // Cập nhật hàm handleFormSubmit để có validation
  const handleFormSubmit = async () => {
    try {
      let isValid = false;

      if (tabValue === 0) {
        isValid = validateMainClassForm();
        if (!isValid) {
          enqueueSnackbar("Vui lòng điền đầy đủ thông tin bắt buộc", {
            variant: "error",
          });
          return;
        }

        // Main class
        if (dialogMode === "create") {
          await axios.post(`${API_URL}/classes/main`, mainClassForm, {
            headers: { Authorization: `Bearer ${token}` },
          });
          enqueueSnackbar("Tạo lớp chính mới thành công", {
            variant: "success",
          });
        } else {
          await axios.put(
            `${API_URL}/classes/main/${selectedClass._id}`,
            mainClassForm,
            {
              headers: { Authorization: `Bearer ${token}` },
            }
          );
          enqueueSnackbar("Cập nhật lớp chính thành công", {
            variant: "success",
          });
        }
      } else {
        isValid = validateTeachingClassForm();
        if (!isValid) {
          enqueueSnackbar("Vui lòng điền đầy đủ thông tin bắt buộc", {
            variant: "error",
          });
          return;
        }

        // Teaching class
        if (dialogMode === "create") {
          await axios.post(`${API_URL}/classes/teaching`, teachingClassForm, {
            headers: { Authorization: `Bearer ${token}` },
          });
          enqueueSnackbar("Tạo lớp giảng dạy mới thành công", {
            variant: "success",
          });
        } else {
          await axios.put(
            `${API_URL}/classes/teaching/${selectedClass._id}`,
            teachingClassForm,
            {
              headers: { Authorization: `Bearer ${token}` },
            }
          );
          enqueueSnackbar("Cập nhật lớp giảng dạy thành công", {
            variant: "success",
          });
        }
      }

      setDialogOpen(false);
      loadClasses();
      loadStats(); // Cập nhật lại thống kê sau khi thao tác
    } catch (error) {
      console.error("Lỗi khi thao tác với lớp học:", error);
      enqueueSnackbar(
        error.response?.data?.message || "Lỗi khi thao tác với lớp học",
        { variant: "error" }
      );
    }
  };

  // Delete handlers
  const openDeleteDialog = (classItem) => {
    setSelectedClass(classItem);
    setDeleteDialogOpen(true);
  };

  // Cập nhật hàm handleDeleteClass để cập nhật thống kê sau khi xóa
  const handleDeleteClass = async () => {
    try {
      const endpoint = tabValue === 0 ? "classes/main" : "classes/teaching";

      await axios.delete(`${API_URL}/${endpoint}/${selectedClass._id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      enqueueSnackbar(
        `Xóa ${tabValue === 0 ? "lớp chính" : "lớp giảng dạy"} thành công`,
        { variant: "success" }
      );
      setDeleteDialogOpen(false);
      loadClasses();
      loadStats(); // Cập nhật lại thống kê sau khi thao tác
    } catch (error) {
      console.error(
        `Lỗi khi xóa ${tabValue === 0 ? "lớp chính" : "lớp giảng dạy"}:`,
        error
      );
      enqueueSnackbar(
        error.response?.data?.message ||
          `Lỗi khi xóa ${tabValue === 0 ? "lớp chính" : "lớp giảng dạy"}`,
        { variant: "error" }
      );
    }
  };

  // Cập nhật hàm loadStats để sử dụng API endpoint mới
  const loadStats = async () => {
    try {
      const mainClassResponse = await axios.get(
        `${API_URL}/classes/main/statistics`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      const teachingClassResponse = await axios.get(
        `${API_URL}/classes/teaching/statistics`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      const userStatsResponse = await axios.get(`${API_URL}/users/stats`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setStats({
        mainClasses: mainClassResponse.data.totalCount || 0,
        teachingClasses: teachingClassResponse.data.totalCount || 0,
        students: userStatsResponse.data.approvedStudents || 0,
        teachers: userStatsResponse.data.approvedTeachers || 0,
      });
    } catch (error) {
      console.error("Lỗi khi tải thống kê:", error);
      // Tiếp tục hiển thị giao diện ngay cả khi không thể tải thống kê
    }
  };

  // Thêm lại hàm handleStudentSelection bị xóa nhầm
  const handleStudentSelection = (event, newValue) => {
    if (tabValue === 0) {
      setMainClassForm({
        ...mainClassForm,
        students: newValue.map((student) => student._id),
      });
    } else {
      setTeachingClassForm({
        ...teachingClassForm,
        selected_students: newValue.map((student) => student._id),
      });
    }
  };

  // Render main class table
  const renderMainClassesTable = () => (
    <TableContainer component={Paper} sx={{ boxShadow: 2 }}>
      <Table sx={{ minWidth: 650 }}>
        <TableHead sx={{ bgcolor: "#f5f5f5" }}>
          <TableRow>
            <TableCell sx={{ fontWeight: "bold" }}>Tên lớp</TableCell>
            <TableCell sx={{ fontWeight: "bold" }}>Mã lớp</TableCell>
            <TableCell sx={{ fontWeight: "bold" }}>Khoa</TableCell>
            <TableCell sx={{ fontWeight: "bold" }}>
              Giáo viên chủ nhiệm
            </TableCell>
            <TableCell align="center" sx={{ fontWeight: "bold" }}>
              Số lượng SV
            </TableCell>
            <TableCell align="right" sx={{ fontWeight: "bold" }}>
              Hành động
            </TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {isLoading ? (
            <TableRow>
              <TableCell colSpan={6} align="center">
                <Box sx={{ display: "flex", justifyContent: "center", p: 3 }}>
                  <CircularProgress size={30} />
                </Box>
              </TableCell>
            </TableRow>
          ) : classes.length === 0 ? (
            <TableRow>
              <TableCell colSpan={6} align="center">
                <Box sx={{ py: 3 }}>
                  <Typography color="textSecondary">
                    Không tìm thấy lớp học nào
                  </Typography>
                  <Button
                    variant="text"
                    color="primary"
                    onClick={clearFilters}
                    sx={{ mt: 1 }}
                  >
                    Xóa bộ lọc
                  </Button>
                </Box>
              </TableCell>
            </TableRow>
          ) : (
            classes.map((classItem) => (
              <TableRow
                key={classItem._id}
                hover
                sx={{ "&:hover": { bgcolor: "rgba(0, 0, 0, 0.04)" } }}
              >
                <TableCell>
                  <Typography variant="body1">{classItem.name}</Typography>
                </TableCell>
                <TableCell>
                  <Chip
                    label={classItem.code}
                    size="small"
                    color="primary"
                    variant="outlined"
                  />
                </TableCell>
                <TableCell>
                  {classItem.department_id?.name ? (
                    <Chip
                      label={classItem.department_id.name}
                      size="small"
                      sx={{ bgcolor: "#e3f2fd" }}
                    />
                  ) : (
                    <Chip
                      label="Chưa phân khoa"
                      size="small"
                      variant="outlined"
                      color="default"
                    />
                  )}
                </TableCell>
                <TableCell>
                  {classItem.advisor_id?.full_name ? (
                    <Box>
                      <Typography variant="body2">
                        {classItem.advisor_id.full_name}
                      </Typography>
                      <Typography variant="caption" color="textSecondary">
                        {classItem.advisor_id.email || ""}
                      </Typography>
                    </Box>
                  ) : (
                    <Chip
                      label="Chưa phân công"
                      size="small"
                      color="warning"
                      variant="outlined"
                    />
                  )}
                </TableCell>
                <TableCell align="center">
                  <Chip
                    label={classItem.students?.length || 0}
                    color={
                      classItem.students?.length > 0 ? "success" : "default"
                    }
                    size="small"
                  />
                </TableCell>
                <TableCell align="right">
                  <Box display="flex" justifyContent="flex-end">
                    <Tooltip title="Sửa lớp">
                      <IconButton
                        color="primary"
                        onClick={() => openDialog("edit", classItem)}
                        size="small"
                        sx={{ mx: 0.5 }}
                      >
                        <Edit fontSize="small" />
                      </IconButton>
                    </Tooltip>
                    <Tooltip title="Xóa lớp">
                      <IconButton
                        color="error"
                        onClick={() => openDeleteDialog(classItem)}
                        size="small"
                        sx={{ mx: 0.5 }}
                      >
                        <Delete fontSize="small" />
                      </IconButton>
                    </Tooltip>
                  </Box>
                </TableCell>
              </TableRow>
            ))
          )}
        </TableBody>
      </Table>
      <TablePagination
        rowsPerPageOptions={[5, 10, 25, 50]}
        component="div"
        count={totalCount}
        rowsPerPage={rowsPerPage}
        page={page}
        onPageChange={handleChangePage}
        onRowsPerPageChange={handleChangeRowsPerPage}
        labelRowsPerPage="Dòng mỗi trang:"
        labelDisplayedRows={({ from, to, count }) =>
          `${from}-${to} trên ${count}`
        }
      />
    </TableContainer>
  );

  // Render teaching class table
  const renderTeachingClassesTable = () => (
    <TableContainer component={Paper} sx={{ boxShadow: 2 }}>
      <Table sx={{ minWidth: 650 }}>
        <TableHead sx={{ bgcolor: "#f5f5f5" }}>
          <TableRow>
            <TableCell sx={{ fontWeight: "bold" }}>Tên lớp</TableCell>
            <TableCell sx={{ fontWeight: "bold" }}>Môn học</TableCell>
            <TableCell sx={{ fontWeight: "bold" }}>Niên khóa</TableCell>
            <TableCell sx={{ fontWeight: "bold" }}>Giáo viên</TableCell>
            <TableCell sx={{ fontWeight: "bold" }}>Lớp chính</TableCell>
            <TableCell align="center" sx={{ fontWeight: "bold" }}>
              Số lượng SV
            </TableCell>
            <TableCell align="right" sx={{ fontWeight: "bold" }}>
              Hành động
            </TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {isLoading ? (
            <TableRow>
              <TableCell colSpan={7} align="center">
                <Box sx={{ display: "flex", justifyContent: "center", p: 3 }}>
                  <CircularProgress size={30} />
                </Box>
              </TableCell>
            </TableRow>
          ) : classes.length === 0 ? (
            <TableRow>
              <TableCell colSpan={7} align="center">
                <Box sx={{ py: 3 }}>
                  <Typography color="textSecondary">
                    Không tìm thấy lớp học nào
                  </Typography>
                  <Button
                    variant="text"
                    color="primary"
                    onClick={clearFilters}
                    sx={{ mt: 1 }}
                  >
                    Xóa bộ lọc
                  </Button>
                </Box>
              </TableCell>
            </TableRow>
          ) : (
            classes.map((classItem) => (
              <TableRow
                key={classItem._id}
                hover
                sx={{ "&:hover": { bgcolor: "rgba(0, 0, 0, 0.04)" } }}
              >
                <TableCell>
                  <Typography variant="body1">
                    {classItem.class_name}
                  </Typography>
                </TableCell>
                <TableCell>
                  {classItem.course_id?.name ? (
                    <Box>
                      <Typography variant="body2">
                        {classItem.course_id.name}
                      </Typography>
                      <Typography variant="caption" color="textSecondary">
                        {classItem.course_id.code || ""}
                      </Typography>
                    </Box>
                  ) : (
                    <Chip label="N/A" size="small" variant="outlined" />
                  )}
                </TableCell>
                <TableCell>
                  <Chip
                    label={`${classItem.semester}/${classItem.academic_year}`}
                    size="small"
                    color="info"
                    variant="outlined"
                  />
                </TableCell>
                <TableCell>
                  {classItem.teacher_id?.full_name ? (
                    <Box>
                      <Typography variant="body2">
                        {classItem.teacher_id.full_name}
                      </Typography>
                      <Typography variant="caption" color="textSecondary">
                        {classItem.teacher_id.email || ""}
                      </Typography>
                    </Box>
                  ) : (
                    <Chip
                      label="Chưa phân công"
                      size="small"
                      color="warning"
                      variant="outlined"
                    />
                  )}
                </TableCell>
                <TableCell>
                  {classItem.main_class_id?.name ? (
                    <Chip
                      label={classItem.main_class_id.name}
                      size="small"
                      sx={{ bgcolor: "#e3f2fd" }}
                    />
                  ) : (
                    <Chip
                      label="Không có"
                      size="small"
                      variant="outlined"
                      color="default"
                    />
                  )}
                </TableCell>
                <TableCell align="center">
                  <Chip
                    label={classItem.students?.length || 0}
                    color={
                      classItem.students?.length > 0 ? "success" : "default"
                    }
                    size="small"
                  />
                </TableCell>
                <TableCell align="right">
                  <Box display="flex" justifyContent="flex-end">
                    <Tooltip title="Sửa lớp">
                      <IconButton
                        color="primary"
                        onClick={() => openDialog("edit", classItem)}
                        size="small"
                        sx={{ mx: 0.5 }}
                      >
                        <Edit fontSize="small" />
                      </IconButton>
                    </Tooltip>
                    <Tooltip title="Xóa lớp">
                      <IconButton
                        color="error"
                        onClick={() => openDeleteDialog(classItem)}
                        size="small"
                        sx={{ mx: 0.5 }}
                      >
                        <Delete fontSize="small" />
                      </IconButton>
                    </Tooltip>
                  </Box>
                </TableCell>
              </TableRow>
            ))
          )}
        </TableBody>
      </Table>
      <TablePagination
        rowsPerPageOptions={[5, 10, 25, 50]}
        component="div"
        count={totalCount}
        rowsPerPage={rowsPerPage}
        page={page}
        onPageChange={handleChangePage}
        onRowsPerPageChange={handleChangeRowsPerPage}
        labelRowsPerPage="Dòng mỗi trang:"
        labelDisplayedRows={({ from, to, count }) =>
          `${from}-${to} trên ${count}`
        }
      />
    </TableContainer>
  );

  // Render main class form dialog
  const renderMainClassFormDialog = () => (
    <Dialog
      open={dialogOpen && tabValue === 0}
      onClose={() => setDialogOpen(false)}
      maxWidth="md"
      fullWidth
    >
      <DialogTitle>
        {dialogMode === "create" ? "Thêm lớp chính mới" : "Sửa lớp chính"}
      </DialogTitle>
      <DialogContent>
        <Grid container spacing={2} sx={{ mt: 1 }}>
          <Grid item xs={12} sm={6}>
            <TextField
              name="name"
              label="Tên lớp"
              fullWidth
              required
              value={mainClassForm.name}
              onChange={handleMainClassFormChange}
              error={formErrors.main.name}
              helperText={
                formErrors.main.name ? "Tên lớp không được để trống" : ""
              }
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              name="code"
              label="Mã lớp"
              fullWidth
              required
              value={mainClassForm.code}
              onChange={handleMainClassFormChange}
              error={formErrors.main.code}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <FormControl fullWidth>
              <InputLabel>Khoa</InputLabel>
              <Select
                name="department_id"
                value={mainClassForm.department_id}
                onChange={handleMainClassFormChange}
                label="Khoa"
              >
                {departments.map((dept) => (
                  <MenuItem key={dept._id} value={dept._id}>
                    {dept.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} sm={6}>
            <Autocomplete
              id="advisor-select"
              options={teachers}
              getOptionLabel={(option) =>
                `${option.full_name} ${option.email ? `(${option.email})` : ""}`
              }
              value={
                teachers.find((t) => t._id === mainClassForm.advisor_id) || null
              }
              onChange={(event, newValue) => {
                setMainClassForm({
                  ...mainClassForm,
                  advisor_id: newValue?._id || "",
                });
              }}
              renderInput={(params) => (
                <TextField
                  {...params}
                  label="Giáo viên chủ nhiệm"
                  variant="outlined"
                  fullWidth
                />
              )}
              renderOption={(props, option) => (
                <li {...props}>
                  <Box>
                    <Typography variant="body1">{option.full_name}</Typography>
                    <Typography variant="body2" color="text.secondary">
                      {option.email}
                    </Typography>
                  </Box>
                </li>
              )}
            />
          </Grid>
          <Grid item xs={12}>
            <Autocomplete
              multiple
              options={students}
              getOptionLabel={(option) =>
                `${option.full_name} (${option.student_code || option.email})`
              }
              value={students.filter((student) =>
                mainClassForm.students.includes(student._id)
              )}
              onChange={handleStudentSelection}
              renderInput={(params) => (
                <TextField
                  {...params}
                  label="Chọn sinh viên"
                  placeholder="Tìm sinh viên"
                />
              )}
              renderTags={(value, getTagProps) =>
                value.map((option, index) => (
                  <Chip
                    label={`${option.full_name} (${
                      option.student_code || option.email
                    })`}
                    {...getTagProps({ index })}
                    key={option._id}
                  />
                ))
              }
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              name="description"
              label="Mô tả"
              fullWidth
              multiline
              rows={3}
              value={mainClassForm.description}
              onChange={handleMainClassFormChange}
            />
          </Grid>
        </Grid>
      </DialogContent>
      <DialogActions>
        <Button onClick={() => setDialogOpen(false)}>Hủy</Button>
        <Button
          onClick={handleFormSubmit}
          variant="contained"
          color="primary"
          disabled={!mainClassForm.name || !mainClassForm.code}
        >
          {dialogMode === "create" ? "Tạo mới" : "Cập nhật"}
        </Button>
      </DialogActions>
    </Dialog>
  );

  // Render teaching class form dialog
  const renderTeachingClassFormDialog = () => (
    <Dialog
      open={dialogOpen && tabValue === 1}
      onClose={() => setDialogOpen(false)}
      maxWidth="md"
      fullWidth
    >
      <DialogTitle>
        {dialogMode === "create"
          ? "Thêm lớp giảng dạy mới"
          : "Sửa lớp giảng dạy"}
      </DialogTitle>
      <DialogContent>
        <Grid container spacing={2} sx={{ mt: 1 }}>
          <Grid item xs={12}>
            <TextField
              name="class_name"
              label="Tên lớp"
              fullWidth
              required
              value={teachingClassForm.class_name}
              onChange={handleTeachingClassFormChange}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <FormControl
              fullWidth
              required
              error={formErrors.teaching.course_id}
            >
              <InputLabel>Môn học</InputLabel>
              <Select
                name="course_id"
                value={teachingClassForm.course_id}
                onChange={handleTeachingClassFormChange}
                label="Môn học"
              >
                {courses.map((course) => (
                  <MenuItem key={course._id} value={course._id}>
                    {course.name}
                  </MenuItem>
                ))}
              </Select>
              {formErrors.teaching.course_id && (
                <FormHelperText>Vui lòng chọn môn học</FormHelperText>
              )}
            </FormControl>
          </Grid>
          <Grid item xs={12} sm={6}>
            <Autocomplete
              id="teacher-select"
              options={teachers}
              getOptionLabel={(option) =>
                `${option.full_name} ${option.email ? `(${option.email})` : ""}`
              }
              value={
                teachers.find((t) => t._id === teachingClassForm.teacher_id) ||
                null
              }
              onChange={(event, newValue) => {
                setTeachingClassForm({
                  ...teachingClassForm,
                  teacher_id: newValue?._id || "",
                });
              }}
              renderInput={(params) => (
                <TextField
                  {...params}
                  label="Giáo viên"
                  variant="outlined"
                  fullWidth
                />
              )}
              renderOption={(props, option) => (
                <li {...props}>
                  <Box>
                    <Typography variant="body1">{option.full_name}</Typography>
                    <Typography variant="body2" color="text.secondary">
                      {option.email}
                    </Typography>
                  </Box>
                </li>
              )}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              name="semester"
              label="Học kỳ"
              fullWidth
              required
              value={teachingClassForm.semester}
              onChange={handleTeachingClassFormChange}
              placeholder="VD: 1, 2, 3"
              error={formErrors.teaching.semester}
              helperText={
                formErrors.teaching.semester ? "Học kỳ không được để trống" : ""
              }
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              name="academic_year"
              label="Năm học"
              fullWidth
              required
              value={teachingClassForm.academic_year}
              onChange={handleTeachingClassFormChange}
              placeholder="VD: 2023-2024"
              error={formErrors.teaching.academic_year}
              helperText={
                formErrors.teaching.academic_year
                  ? "Năm học không được để trống"
                  : ""
              }
            />
          </Grid>
          <Grid item xs={12} sm={12}>
            <FormControl fullWidth>
              <InputLabel>Lớp chính</InputLabel>
              <Select
                name="main_class_id"
                value={teachingClassForm.main_class_id}
                onChange={handleTeachingClassFormChange}
                label="Lớp chính"
              >
                <MenuItem value="">Không chọn</MenuItem>
                {/* Cần tải danh sách lớp chính từ API */}
                {classes.map((mainClass) => (
                  <MenuItem key={mainClass._id} value={mainClass._id}>
                    {mainClass.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12}>
            <Autocomplete
              multiple
              options={students}
              getOptionLabel={(option) =>
                `${option.full_name} (${option.student_code || option.email})`
              }
              value={students.filter((student) =>
                teachingClassForm.selected_students.includes(student._id)
              )}
              onChange={handleStudentSelection}
              renderInput={(params) => (
                <TextField
                  {...params}
                  label="Chọn sinh viên"
                  placeholder="Tìm sinh viên"
                />
              )}
              renderTags={(value, getTagProps) =>
                value.map((option, index) => (
                  <Chip
                    label={`${option.full_name} (${
                      option.student_code || option.email
                    })`}
                    {...getTagProps({ index })}
                    key={option._id}
                  />
                ))
              }
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              name="description"
              label="Mô tả"
              fullWidth
              multiline
              rows={3}
              value={teachingClassForm.description}
              onChange={handleTeachingClassFormChange}
            />
          </Grid>
        </Grid>
      </DialogContent>
      <DialogActions>
        <Button onClick={() => setDialogOpen(false)}>Hủy</Button>
        <Button
          onClick={handleFormSubmit}
          variant="contained"
          color="primary"
          disabled={
            !teachingClassForm.class_name ||
            !teachingClassForm.course_id ||
            !teachingClassForm.semester ||
            !teachingClassForm.academic_year
          }
        >
          {dialogMode === "create" ? "Tạo mới" : "Cập nhật"}
        </Button>
      </DialogActions>
    </Dialog>
  );

  // Thêm component hiển thị thống kê
  const renderStatCards = () => (
    <Grid container spacing={2} sx={{ mb: 3 }}>
      <Grid item xs={12} sm={6} md={3}>
        <Card
          sx={{
            borderLeft: "4px solid #3f51b5",
            boxShadow: 2,
            height: "100%",
            display: "flex",
            flexDirection: "column",
          }}
        >
          <CardContent>
            <Typography variant="h6" color="textSecondary" gutterBottom>
              Lớp chính
            </Typography>
            <Typography variant="h4">{stats.mainClasses}</Typography>
          </CardContent>
          <Box sx={{ flexGrow: 1 }} />
          <Box
            sx={{
              p: 1.5,
              bgcolor: "rgba(63, 81, 181, 0.08)",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              cursor: "pointer",
            }}
            onClick={() => {
              setTabValue(0);
              clearFilters();
            }}
          >
            <School fontSize="small" sx={{ mr: 1 }} />
            <Typography variant="body2">Xem tất cả lớp chính</Typography>
          </Box>
        </Card>
      </Grid>

      <Grid item xs={12} sm={6} md={3}>
        <Card
          sx={{
            borderLeft: "4px solid #f50057",
            boxShadow: 2,
            height: "100%",
            display: "flex",
            flexDirection: "column",
          }}
        >
          <CardContent>
            <Typography variant="h6" color="textSecondary" gutterBottom>
              Lớp giảng dạy
            </Typography>
            <Typography variant="h4">{stats.teachingClasses}</Typography>
          </CardContent>
          <Box sx={{ flexGrow: 1 }} />
          <Box
            sx={{
              p: 1.5,
              bgcolor: "rgba(245, 0, 87, 0.08)",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              cursor: "pointer",
            }}
            onClick={() => {
              setTabValue(1);
              clearFilters();
            }}
          >
            <People fontSize="small" sx={{ mr: 1 }} />
            <Typography variant="body2">Xem tất cả lớp giảng dạy</Typography>
          </Box>
        </Card>
      </Grid>

      <Grid item xs={12} sm={6} md={3}>
        <Card
          sx={{
            borderLeft: "4px solid #4caf50",
            boxShadow: 2,
            height: "100%",
          }}
        >
          <CardContent>
            <Typography variant="h6" color="textSecondary" gutterBottom>
              Sinh viên
            </Typography>
            <Typography variant="h4">{stats.students}</Typography>
            <Typography variant="body2" color="textSecondary" sx={{ mt: 1 }}>
              Tổng số sinh viên đã phê duyệt
            </Typography>
          </CardContent>
        </Card>
      </Grid>

      <Grid item xs={12} sm={6} md={3}>
        <Card
          sx={{
            borderLeft: "4px solid #ff9800",
            boxShadow: 2,
            height: "100%",
          }}
        >
          <CardContent>
            <Typography variant="h6" color="textSecondary" gutterBottom>
              Giáo viên
            </Typography>
            <Typography variant="h4">{stats.teachers}</Typography>
            <Typography variant="body2" color="textSecondary" sx={{ mt: 1 }}>
              Tổng số giáo viên đã phê duyệt
            </Typography>
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  );

  // Thêm component hiển thị thẻ thông tin khi không có dữ liệu
  const renderEmptyState = () => (
    <Box
      sx={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        py: 5,
        px: 2,
        bgcolor: "#f5f5f5",
        borderRadius: 2,
      }}
    >
      <Box sx={{ mb: 2 }}>
        {tabValue === 0 ? (
          <School sx={{ fontSize: 60, color: "#3f51b5" }} />
        ) : (
          <People sx={{ fontSize: 60, color: "#f50057" }} />
        )}
      </Box>
      <Typography variant="h6" gutterBottom>
        {tabValue === 0 ? "Chưa có lớp chính nào" : "Chưa có lớp giảng dạy nào"}
      </Typography>
      <Typography
        variant="body2"
        color="textSecondary"
        align="center"
        sx={{ mb: 3 }}
      >
        {tabValue === 0
          ? "Lớp chính dùng để quản lý danh sách sinh viên theo khóa học và khoa phòng. Mỗi lớp chính có một giáo viên chủ nhiệm."
          : "Lớp giảng dạy dùng để quản lý các môn học cụ thể. Mỗi lớp giảng dạy có một giáo viên giảng dạy và có thể liên kết với lớp chính."}
      </Typography>
      <Button
        variant="contained"
        color="primary"
        startIcon={<Add />}
        onClick={() => openDialog("create")}
      >
        {tabValue === 0 ? "Tạo lớp chính mới" : "Tạo lớp giảng dạy mới"}
      </Button>
    </Box>
  );

  return (
    <Box>
      <Typography variant="h5" gutterBottom>
        Quản lý lớp học
      </Typography>

      {/* Thống kê */}
      {renderStatCards()}

      <Paper sx={{ mb: 3 }}>
        <Tabs
          value={tabValue}
          onChange={handleTabChange}
          indicatorColor="primary"
          textColor="primary"
          variant="fullWidth"
        >
          <Tab label="Lớp chính" icon={<School />} iconPosition="start" />
          <Tab label="Lớp giảng dạy" icon={<People />} iconPosition="start" />
        </Tabs>
      </Paper>

      <Paper sx={{ p: 2, mb: 3 }}>
        <Box
          display="flex"
          flexDirection={{ xs: "column", sm: "row" }}
          alignItems={{ sm: "center" }}
          justifyContent="space-between"
          gap={2}
          mb={2}
        >
          <Box display="flex" alignItems="center" gap={1} flexGrow={1}>
            <TextField
              label={`Tìm kiếm ${
                tabValue === 0 ? "lớp chính" : "lớp giảng dạy"
              }`}
              variant="outlined"
              size="small"
              fullWidth
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              onKeyPress={(e) => e.key === "Enter" && handleSearch()}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <Search />
                  </InputAdornment>
                ),
              }}
            />
            <Button
              variant="contained"
              color="primary"
              onClick={handleSearch}
              startIcon={<Search />}
            >
              Tìm
            </Button>
          </Box>

          <Box display="flex" gap={2}>
            <Button
              variant="outlined"
              startIcon={<Refresh />}
              onClick={loadClasses}
            >
              Làm mới
            </Button>
            <Button
              variant="contained"
              startIcon={<Add />}
              onClick={() => openDialog("create")}
            >
              Thêm {tabValue === 0 ? "lớp chính" : "lớp giảng dạy"}
            </Button>
          </Box>
        </Box>

        {/* Bộ lọc nâng cao */}
        <Collapse in={true}>
          <Box
            sx={{ p: 1, border: "1px solid #e0e0e0", borderRadius: 1, mt: 1 }}
          >
            <Typography variant="subtitle2" gutterBottom>
              Bộ lọc nâng cao
            </Typography>
            <Grid container spacing={2}>
              {tabValue === 0 ? (
                // Bộ lọc cho lớp chính
                <>
                  <Grid item xs={12} sm={6} md={3}>
                    <FormControl fullWidth size="small">
                      <InputLabel>Khoa/Bộ môn</InputLabel>
                      <Select
                        name="department"
                        value={filterOptions.department}
                        onChange={handleFilterChange}
                        label="Khoa/Bộ môn"
                      >
                        <MenuItem value="">Tất cả</MenuItem>
                        {departments.map((dept) => (
                          <MenuItem key={dept._id} value={dept._id}>
                            {dept.name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12} sm={6} md={3}>
                    <FormControl fullWidth size="small">
                      <InputLabel>Giáo viên chủ nhiệm</InputLabel>
                      <Select
                        name="teacher"
                        value={filterOptions.teacher}
                        onChange={handleFilterChange}
                        label="Giáo viên chủ nhiệm"
                      >
                        <MenuItem value="">Tất cả</MenuItem>
                        {teachers.map((teacher) => (
                          <MenuItem key={teacher._id} value={teacher._id}>
                            {teacher.full_name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                </>
              ) : (
                // Bộ lọc cho lớp giảng dạy
                <>
                  <Grid item xs={12} sm={6} md={3}>
                    <FormControl fullWidth size="small">
                      <InputLabel>Môn học</InputLabel>
                      <Select
                        name="course"
                        value={filterOptions.course}
                        onChange={handleFilterChange}
                        label="Môn học"
                      >
                        <MenuItem value="">Tất cả</MenuItem>
                        {courses.map((course) => (
                          <MenuItem key={course._id} value={course._id}>
                            {course.name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12} sm={6} md={3}>
                    <FormControl fullWidth size="small">
                      <InputLabel>Giáo viên</InputLabel>
                      <Select
                        name="teacher"
                        value={filterOptions.teacher}
                        onChange={handleFilterChange}
                        label="Giáo viên"
                      >
                        <MenuItem value="">Tất cả</MenuItem>
                        {teachers.map((teacher) => (
                          <MenuItem key={teacher._id} value={teacher._id}>
                            {teacher.full_name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12} sm={6} md={3}>
                    <TextField
                      fullWidth
                      size="small"
                      label="Học kỳ"
                      name="semester"
                      value={filterOptions.semester}
                      onChange={handleFilterChange}
                    />
                  </Grid>
                </>
              )}
              <Grid item xs={12} sm={6} md={3}>
                <Box display="flex" gap={1}>
                  <Button
                    variant="outlined"
                    color="primary"
                    fullWidth
                    onClick={applyFilters}
                  >
                    Áp dụng
                  </Button>
                  <Button
                    variant="outlined"
                    color="inherit"
                    onClick={clearFilters}
                  >
                    Xóa lọc
                  </Button>
                </Box>
              </Grid>
            </Grid>
          </Box>
        </Collapse>
      </Paper>

      {/* Hiển thị kết quả hoặc trạng thái trống */}
      {isLoading ? (
        <Box sx={{ display: "flex", justifyContent: "center", py: 8 }}>
          <CircularProgress />
        </Box>
      ) : classes.length === 0 &&
        searchTerm === "" &&
        !filterOptions.department &&
        !filterOptions.teacher &&
        !filterOptions.course &&
        !filterOptions.semester ? (
        renderEmptyState()
      ) : tabValue === 0 ? (
        renderMainClassesTable()
      ) : (
        renderTeachingClassesTable()
      )}

      {/* Dialog forms */}
      {tabValue === 0
        ? renderMainClassFormDialog()
        : renderTeachingClassFormDialog()}

      {/* Delete confirmation dialog */}
      <Dialog
        open={deleteDialogOpen}
        onClose={() => setDeleteDialogOpen(false)}
      >
        <DialogTitle>
          Xác nhận xóa {tabValue === 0 ? "lớp chính" : "lớp giảng dạy"}
        </DialogTitle>
        <DialogContent>
          <DialogContentText>
            Bạn có chắc chắn muốn xóa{" "}
            {tabValue === 0
              ? `lớp chính "${selectedClass?.name}"`
              : `lớp giảng dạy "${selectedClass?.class_name}"`}
            ? Hành động này không thể hoàn tác.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDeleteDialogOpen(false)}>Hủy</Button>
          <Button onClick={handleDeleteClass} variant="contained" color="error">
            Xóa
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default ClassesPage;
